function launchIDPAuth($rootScope, uname, pass, authsuccess, autherror, $http) {

  var clientid = "LvRUEfuDt5xcz3reuDkPoPIouhEa";
  var clientsecret = "zorp9LIIFQ3fi5y2MIq5Xp0WMU8a";

  $rootScope.getUserInfo = function(token, callback) {
    //alert("Calling userinfo with : " + token);
    var url = "https://idp.quicklaunchsso.com" + "/oauth2/userinfo";
    $http({
      method: 'GET',
      url: url,
      data: {},
      headers: {
        'Authorization': 'Bearer ' + token
      }
    }).success(function(data, status, headers, config) {
      //alert("User Info " + JSON.stringify(data));
      callback(data);
    }).error(function(data, status, headers, config) {
      //alert("Error : " + JSON.stringify(data));
      $.unblockUI();
    });
  };
  $rootScope.fetchIDPToken = function(accesscode, callback) {
    $.blockUI();
    var url = "https://idp.quicklaunchsso.com" + "/oauth2/token";
    var postdata = "grant_type=authorization_code&client_id=" + clientid + "&client_secret=" + clientsecret +
      "&redirect_uri=" + encodeURIComponent("https://uta-staging.unifyed.com/auth/unifyd/passport/callback") + "&code=" + accesscode;

    //alert(postdata);
    $http({
      method: 'POST',
      url: url,
      data: postdata,
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    }).success(function(data, status, headers, config) {
      try {
        //alert("Data " + JSON.stringify(data));
        //$.jStorage.set("IDPAuthToken", data);
        $.unblockUI();
        callback(data);
      } catch (ex) {
        //alert(ex);
      }
    }).error(function(data, status, headers, config) {
      //alert("Error : " + JSON.stringify(data));
      $.unblockUI();
    });

  };

  //var launchurl = "https://idp-preprod.quicklaunchsso.com/oauth2/authorize?response_type=code&redirect_uri=https://uta-staging.unifyed.com/auth/unifyd/passport/callback&scope=openid";
  var launchurl = "https://idp.quicklaunchsso.com/oauth2/authorize?response_type=code&client_id=LvRUEfuDt5xcz3reuDkPoPIouhEa&redirect_uri=" + encodeURIComponent("https://uta-staging.unifyed.com/auth/unifyd/passport/callback") + "&scope=openid";
  var iabRef = window.open(launchurl, "_blank", "location=yes,hidden=yes,clearcache=yes,enableViewportScale=yes");
  var loadStop = null;
  var loadStart = function(data) {
    //alert(data.url);
    if (data.url.indexOf("/auth/unifyd/passport/callback?code=") != -1) {
      iabRef.removeEventListener("loadstart", loadStart);
      iabRef.removeEventListener("loadstop", loadStop);
      iabRef.close();
      //alert(data.url);
      var authcode = data.url.split("?")[1].split("&")[0].split("=")[1];
      //alert(authcode);
      $.jStorage.set("IDPTokenCode", authcode);
      try {
        $rootScope.fetchIDPToken(authcode, function(accesstoken) {
          $rootScope.ticket = accesstoken;
          // alert("Token : " + accesstoken);
          //alert("Extracted token :  " + accesstoken.access_token);
          $rootScope.getUserInfo(accesstoken.access_token, function(userinfodata) {
            $.jStorage.set('username', uname);
            $.jStorage.set('password', pass);
            $.jStorage.set('ticket', accesstoken);
            $.jStorage.set('userinfo', userinfodata);
            //alert("before calling authsuccess " + $rootScope.loggedin);
            try {
              authsuccess({
                ticket: accesstoken,
                roles: []
              });
            } catch (err) {
              //alert(err);
            }

          });
        });
      } catch (ex) {
        //alert(ex);
      }
    }
  };
  iabRef.addEventListener("loadstart", loadStart);

  var username = uname;
  var password = pass;
  var cd = "document.getElementById('IDToken1').value = '" + username + "';document.getElementById('IDToken2').value = '" + password + "';document.getElementsByName('Login.Submit')[0].click();";
  var cb = function(data) {};
  loadStop = function(data) {

    if (data.url.indexOf("error=") != -1) {
      iabRef.close();
      //alert(" Your UT EID or password was incorrect. ");
      authsuccess({
        error: "Your UT EID or password was incorrect."
      });
      iabRef.removeEventListener("loadstart", loadStart);
      iabRef.removeEventListener("loadstop", loadstop);

    } else {
      iabRef.executeScript({
        code: cd
      }, cb);
    }
  };
  iabRef.addEventListener("loadstop", loadStop);
}

