function pageprocessorSettings2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  function assignUserDefaultsFromDB(userData) {
    $rootScope.user.whatsupChannels = userData.whatsupChannels;
    $rootScope.user.giveToChannels = userData.giveToChannels;
    $rootScope.user.messageChannels = userData.messageChannels;
    if (userData.canvasInfo) $rootScope.user.canvasInfo = userData.canvasInfo;
    $rootScope.user.channels = userData.channels;
    if (userData.phone) $rootScope.user.phone = userData.phone;
  }


  if ($rootScope.loggedIn || $rootScope.webApp) {
    var url = '/unifydidentity/user/search/findOneByEmail?email=' + $rootScope.user['email'];
    //$scope.channel = false;
    $scope.messageChannelEmail = false;
    $scope.whatsupChannelEmail = false;
    $scope.giveToChannel = false;
    $scope.messageChannelSms = false;


    $rootScope.callAPI(url, 'GET', {}, function(res) {
      $scope.data = res.data;
      assignUserDefaultsFromDB($scope.data);
      //  $scope.channel = $scope.data.channels.indexOf('email') !== -1;
      $scope.data.whatsupChannels = $scope.data.whatsupChannels == null ? [] : $scope.data.whatsupChannels;
      //  $scope.data.messageChannelEmail = $scope.data.messageChannelEmail == null ? [] : $scope.data.messageChannelEmail;
      $scope.data.giveToChannel = $scope.data.giveToChannel == null ? [] : $scope.data.giveToChannel;
      $scope.messageChannelEmail = ($scope.data.messageChannels) ? $scope.data.messageChannels.indexOf('email') !== -1 : false;
      $scope.whatsupChannelEmail = ($scope.data.whatsupChannels) ? $scope.data.whatsupChannels.indexOf('email') !== -1 : false;
      $scope.giveToChannel = ($scope.data.giveToChannels) ? $scope.data.giveToChannels.indexOf('email') !== -1 : false;
      $scope.messageChannelSms = ($scope.data.messageChannels) ? $scope.data.messageChannels.indexOf('sms') !== -1 : false;
      $scope.data.messageChannels = ($scope.data.messageChannels) ? $scope.data.messageChannels : [];

    });
  }


  $scope.resetApp = function(btn) {
    if (btn == 1) {
      var tenant = MyCampusApp.config.tenant;
      $.jStorage.deleteKey(tenant + '-metadata');
