          $.jStorage.deleteKey("UserProfile");

          $.jStorage.deleteKey("profileData");
        } else {

        }
      }
      
    } catch (e) {
      alert(e)
    }
  }
  
  $scope.getAppletVersion = function(){
    $location.path('/app/Settings2/Settings2Page14');
  }
  
}

