function pageprocessorSettings2Page12(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.assignMessagingGroups = function(messagingGroupDetails) {
    $rootScope.messagingGroupDetails = messagingGroupDetails

    $location.path("/app/Settings2/Settings2Page13")
  }

  var rbacGroupsApi = function() {
    var url = '/unifyedrbac/rbac';
    $rootScope.callAPI(url, 'GET', {}, function(res) {

      if (res.data) {
        $scope.rbacGroup = [];

        angular.forEach(res.data.content, function(value) {
          $scope.rbacGroup.push({
            "rbacGroupId": value._id,
            "rbacGroupName": value.role,
            "usergroups": null,
            "users": null,
            updatedDate: null
          })
        });

        var url = '/unifydmessaging/messagingadmin'

        $rootScope.callAPI(url, 'GET', {}, function(res) {
          angular.forEach(res.data, function(permissions) {
            var groupIndex = $scope.rbacGroup.findIndex(function(group) {return (group != null && group.rbacGroupId == permissions.rbacGroupId)});

            if (groupIndex != -1) {
              $scope.rbacGroup[groupIndex].id = permissions.id
              $scope.rbacGroup[groupIndex].usergroups = permissions.usergroups
              $scope.rbacGroup[groupIndex].users = permissions.users
              $scope.rbacGroup[groupIndex].updatedDate = permissions.updatedDate
              $scope.rbacGroup[groupIndex].totalGroups = permissions.usergroups ? permissions.usergroups.length : 0
              $scope.rbacGroup[groupIndex].totalUsers = permissions.users ? permissions.users.length : 0
            }
          });

        })
      }
    });
  }

  rbacGroupsApi();
  $scope.openPrevPage = function() {
    $location.path("/app/Settings2/Settings2");
  };
}

function pageprocessorSettings2Page13(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $rootScope.groupsMessagingSetting = "";
  $scope.external = false
  $scope.allGroupValue = false
  var putPost = "PUT"

  if (!$rootScope.messagingGroupDetails) {
    $location.path("/app/Settings2/Settings2Page12")
  }

  $scope.userMessagingSearchResult = {}

  let createMessagingGroup = function() {
    let groupArray = []

    $rootScope.messagingGroupDetails.usergroups.forEach(function(group) {
      groupArray.push(group.userGroupName)
    })

    return groupArray
  }

  let createMessagingUser = function() {
    let userArray = []

    // $rootScope.messagingGroupDetails.users.forEach(function(user) {
    //   userArray.push(user.username)
    // })

    $rootScope.messagingGroupDetails.users.forEach(function(user) {
      userArray.push({
        "email": user.username,
        "name": user.name
      })
    })

    return userArray
  }

  let createCheckMessagingList = function() {
    let userArray = []

    $rootScope.messagingGroupDetails.users.forEach(function(user) {
      userArray.push(user.username)
    })

    return userArray
  }

  $rootScope.selectedMessagingGroup = $rootScope.messagingGroupDetails.usergroups ? createMessagingGroup() : []
  $rootScope.selectedMessagingUsers = $rootScope.messagingGroupDetails.users ? createMessagingUser() : []
  $rootScope.checkMessagingList = $rootScope.messagingGroupDetails.users ? createCheckMessagingList() : []


  if (!$rootScope.messagingGroupDetails.users && !$rootScope.messagingGroupDetails.usergroups) {
    putPost = "PUT"
  }

  $scope.validateAllGroup = function() {
    var flag = true;
    var field = ($scope.external) ? "roleName" : "group";
    if ($rootScope.groupsMessagingAdmin && $rootScope.selectedMessagingGroup) {
      for (var i = 0; i < $rootScope.groupsMessagingAdmin.length; i++) {
        if ($rootScope.selectedMessagingGroup.indexOf($rootScope.groupsMessagingAdmin[i][field]) === -1) {
          flag = false;
          break;
        }
      }
    }

    return flag;
  }

  var specialChars = "<>!#$%^&*()+[]{}?:;|'\"\\,/~`=";

  $scope.checkForSpecialChar = function(string) {
    for (let i = 0; i < specialChars.length; i++) {
      if (string.indexOf(specialChars[i]) > -1) {
        return true
      }
    }
    return false;
  }

  $scope.searchUsersMatching = function() {
    var str = $scope.userSearchEmail;
    var queryParam = ($scope.external) ? "&external=external" : "";
    var url = "/unifydidentity/open/users?userId=" + str + queryParam;
    $rootScope.callAPI(url, 'GET', {}, function(res) {
      $scope.userMessagingSearchResult.users = (res && res.data) ? res.data : [];
      $scope.userMessagingSearchResult.last = true;
      $scope.userMessagingSearchResult.page = 1;
    })
  }


  $scope.searchUser = function(page, event) {
    $('#msgNoDataFound').show();
    $scope.showErrorMessage = false;
    $scope.errorMessage = "";
    $scope.userMessagingSearchResult.users = "";

    if (event) {
      event.stopPropagation();
      event.preventDefault();
    }
    if ($scope.userSearchEmail.length < 3) {
      $scope.showErrorMessage = true;
      $scope.errorMessage = "Please enter at least 3 characters";
      return false;
    }
    if ($scope.checkForSpecialChar($scope.userSearchEmail)) {
      $scope.showErrorMessage = true;
      $scope.errorMessage = "No data found";
      return;
    }

    if (!page)
      page = 0;
    else
      page++;

    var userStr = $scope.userSearchEmail.toLowerCase();
    var url = '/unifydidentity/user/search/findByName?sort=email,ASC&name=' + userStr + '&page=' + page;
    /*var url = '/unifydidentity/user/search/findByEmailStartingWith?sort=email,ASC&email=' + userStr + '&page=' + page; */

    $rootScope.isblocking = true;
    $rootScope.callAPI(url, 'GET', {}, function(res) {
      $rootScope.isblocking = false;
      var data = res.data;

      if (data.content.length == 0) {
        $scope.showErrorMessage = true;
        $scope.errorMessage = "No data found";
      }

      //var addSearch = $rootScope.selectedMessagingUsers.findIndex(x => x.email == data['content'])

      $scope.userMessagingSearchResult.users = data['content'];
      $scope.userMessagingSearchResult.page = data['number'];
      $scope.userMessagingSearchResult.last = data['last'];
    })

  };


  $scope.individualUserSearch = function() {
    setTimeout(function() {
      $("#IndividualUserTxt").focus();
    }, 200);
  };

  $scope.addUser = function(e, event) {
    event.stopPropagation();
    event.preventDefault();
    $scope.userMessagingSearchResult = {}
      //$rootScope.selectedUsersMessaging.push(($scope.external) ? e : e.email);
    $rootScope.checkMessagingList.push(e.email);
    $rootScope.selectedMessagingUsers.push(($scope.external) ? e : e);
  };

  $scope.removeUser = function(index, e) {
    e.stopPropagation();
    e.preventDefault();
    $rootScope.checkMessagingList.splice(index, 1);
    $rootScope.selectedMessagingUsers.splice(index, 1);
  };

  $scope.allGroupToggle = function() {
    var field = ($scope.external) ? "roleName" : "group";

    if ($scope.allGroupValue) {
      $rootScope.selectedMessagingGroup = [];
      for (var i = 0; i < $rootScope.groupsMessagingAdmin.length; i++) {
        $rootScope.selectedMessagingGroup.push($rootScope.groupsMessagingAdmin[i][field]);
      }
    } else {
      $rootScope.selectedMessagingGroup = [];
    }
  }

  $scope.groupSelected = function(g, e) {
    e.stopPropagation();
    e.preventDefault();

    var field = ($scope.external) ? "roleName" : "group";
    if (g === 'All') {

      if (!$scope.validateAllGroup()) {
        $scope.allGroupValue = true
        $rootScope.selectedMessagingGroup = [];
        for (var i = 0; i < $rootScope.groupsMessagingAdmin.length; i++) {
          $rootScope.selectedMessagingGroup.push($rootScope.groupsMessagingAdmin[i][field]);
        }
      } else {
        $scope.allGroupValue = false
        $rootScope.selectedMessagingGroup = [];
      }
    } else {
      $scope.allGroupValue = false

      var index = $rootScope.selectedMessagingGroup.indexOf(g[field]);
      if (index === -1) {
        $rootScope.selectedMessagingGroup.push(g[field]);
      } else {
        $rootScope.selectedMessagingGroup.splice(index, 1);
      }
    }
  };

  try {
    if (!$rootScope.groupsMessagingAdmin) {

      var groupsUrl = '/unifydplatform/open/groups';
      $rootScope.isblocking = true;
      $.blockUI();
      $rootScope.callAPI(groupsUrl, 'GET', {}, function(res) {
        $rootScope.groupsMessagingAdmin = res.data;
        $rootScope.isblocking = false;

        if ($scope.validateAllGroup()) {
          $scope.allGroupValue = true
        }

        $.unblockUI();
      });
    } else {

      if ($scope.validateAllGroup()) {
        $scope.allGroupValue = true
      }
    }
  } catch (e) {
    console.log(e)
  }

  $scope.showAlert = function(message) {
    if (window.device) {
      navigator.notification.alert(message, null, ' ', 'Ok');
    } else {
      //alert("Invalid youtube url.");
      $.alert({
        animation: 'none',
        title: '',
        content: message,
        columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
        buttons: {
          OK: {
            'btnClass': 'btn-primary boxradius popBtns focusTranspatent',
            'action': function() {
              //alert('cancel');
            }
          }
        }
      });
    }

  };

  $scope.saveMessagingSettings = function() {
    $rootScope.isblocking = true
    $.blockUI()

    let groupsArray = []
    let usersArray = []

   /* if ($rootScope.selectedMessagingGroup.length === 0) {
      $scope.showAlert("Select at least one User Group.");
      $rootScope.isblocking = false
      $.unblockUI()
      return;
    }*/

    //Creating User and Group Messaging Array
    $rootScope.selectedMessagingGroup.forEach(function(gro) {
      groupIndex = $rootScope.groupsMessagingAdmin.findIndex(function(mess) {
        return (mess != null && mess.group == gro)
      });
      groupsArray.push({
        "userGroupName": $rootScope.groupsMessagingAdmin[groupIndex].group,
        "userGroupId": $rootScope.groupsMessagingAdmin[groupIndex].id
      })
    })

    $rootScope.messagingGroupDetails.usergroups = groupsArray

    $rootScope.selectedMessagingUsers.forEach(function(user) {
      usersArray.push({
        "username": user.email,
        "userId": null,
        "name": user.firstName + " " + user.lastName,
      })
    })

    $rootScope.messagingGroupDetails.users = usersArray

    var url = '/unifydmessaging/messagingadmin'

    $rootScope.callAPI(url, putPost, $rootScope.messagingGroupDetails, function(res) {
      $rootScope.isblocking = false
      $.unblockUI()
      $location.path("/app/Settings2/Settings2Page12")
    })
  }

  $scope.cancelMessagingSettings = function() {
    $location.path("/app/Settings2/Settings2Page12");
  }


  $scope.openPrevPage = function() {
    $location.path("/app/Settings2/Settings2Page12");
  };

}

