  $scope.callAutomatedAPI = function(endpoint, method, body, callBack) {
    var serviceUrl = smartAlertsUrl + endpoint;
    var url = "";
    var proxyMethod = method;
    var proxyData = {};
    /*
     if (window.device) {
     url = serviceUrl;
     } else {
     url = "/websimulator/json?url=" + encodeURIComponent(serviceUrl);
     proxyMethod = "POST";
     proxyData = {
     method: method,
     body: body
     }
     }

     $http({
     method: proxyMethod,
     url: url,
     data: proxyData
     }).then(function successCallback(res) {
     //console.log("RESULTS:");
     //console.log(res.data);
     return callBack(res.data);
     }, function errorCallback(err) {
     //console.error(err)
     return callBack({"error": "Error executing API"});
     });
     */
    var headers = {
      "Content-Type": "application/json"
    }
    url = "/proxy";
    var postbody = {
      url: serviceUrl,
      method: method,
      headers: headers,
      json: body
    }
    $http({
      method: "POST",
      url: url,
      data: postbody
    }).then(function successCallBack(res) {
      return callBack(res.data);
    }, function errorCallback(err) {
      return callBack({
        "error": "Error executing API"
      });
    });
  }

