      $('#connectCarousel').addClass('active messagePreview');
    } else if ($scope.selChannel == "sms") {
      $('#smsCarousel').addClass('active messagePreview');
    } else if ($scope.selChannel == "email") {
      $('#emailCarousel').addClass('active messagePreview');
    }

    if (message != undefined && message) {
      $scope.messageData = message;
      $scope.channelCount = message.channels.length;
      console.log("test", $scope.channelCount);
      $scope.emailmessage = message.channelMsg["email"];
      $scope.connectmessage = message.channelMsg["whatsUp"];
      $scope.smsmessage = message.channelMsg["sms"];
      if (message.title)
        $scope.emailtitle = message.title;

      if (message.graphicType == "image")
        $scope.postImage = message.graphicUrl;
      else if (message.graphicType == "video")
        $scope.youtubeurl = message.graphicUrl;

      console.log("tags", message.tags)
      if (message.tags != undefined) {
        if (message.tags.length > 0) {
          var url = '/unifyedcategory/category';
          $rootScope.callAPI(url, 'GET', {}, function(res) {
            $scope.connectCategory = res['data']['_embedded']['category'];
            console.log("connectCategory", $scope.connectCategory)
            var category = []
            for (var i = 0; i < $scope.connectCategory.length; i++) {
              if (message.tags.indexOf($scope.connectCategory[i].id) != -1 || message.tags.indexOf($scope.connectCategory[i].category) != -1) {
                category.push($scope.connectCategory[i].category)
              }
            }
            $scope.connectTagsNames = category;

          });
        }
      }

      var url = '/unifydidentity/user/search/findOneByEmail?email=' + message.createdBy;
      $rootScope.callAPI(url, 'GET', {}, function(res) {
        if (res && res.data) {
          $scope.user = res.data
        } else {
          $scope.user = {};
        }
      });

    } else {

      $scope.messageData = {};

    }

    setTimeout(function() {
      if (!$scope.$$phase) {
        console.log("Before calling apply..");
        try {
          $scope.$apply();
        } catch (ex) {}

      }

      $.unblockUI();

    }, 100)
    var owlStageTransform = $('.owl-stage').attr('style');
    var owlOuterStageTransform = $('.owl-stage-outer').attr('style');
    $('.owl-stage').css('transform', 'initial');
    $('.owl-stage-outer').css('transform', 'initial');
    //fix for app
    $('.app-body').removeClass('animated');
    $('.app').css('position', 'initial');

    $('#channelPreview').on('click', 'hidden.bs.modal', function() {
      $('.owl-stage').attr('style', owlStageTransform);
      $('.owl-stage-outer').attr('style', owlOuterStageTransform);
      $('.app-body').addClass('animated');
      $('.app').css('position', 'relative');

    });
    $('.modal-backdrop').click(function() {
      $('.owl-stage').attr('style', owlStageTransform);
      $('.owl-stage-outer').attr('style', owlOuterStageTransform);
      $('.app-body').addClass('animated');
      $('.app').css('position', 'relative');
    });

  }

