    $scope.searchAdditionalGroups = function(roleNameStr) {
      return;
      var queryParam = ($scope.external) ? "&external=external" : "";
      var groupsUrl = '/unifydplatform/open/groups?groupName=' + roleNameStr + queryParam;
      $rootScope.callAPI(groupsUrl, 'GET', {}, function(res) {
        if (res && res.data) {
          angular.forEach(res.data, function(value, key) {
            var found = false;
            if ($scope.external) {
              angular.forEach($rootScope.groupsMessaging, function(val, k) {
                if (val.roleName == value.roleName) {
                  found = true;
