        var messageBody = {
          external: $scope.external,
          groups: $rootScope.selectedGroupMessaging,
          individualUser: $rootScope.selectedUsersMessaging,
          gender: [], // $rootScope.selectedGenderMessaging,
          title: unescape($scope.title),
          defaultMessage: unescape($scope.description),
          channels: $rootScope.selectedChannelsMessaging,
          scheduledMsg: !$scope.publishNow,
          important: $scope.priority === 'High',
          reqAck: $scope.requireAck,
          override: $scope.overrideUser,
          tags: $scope.connectTagsName || [],
          sent: true
        };


        if (!$scope.publishNow) {
          var tDate = new Date();
          var currentDate = new Date(tDate.getFullYear(), tDate.getMonth(), tDate.getDate(), tDate.getHours(), tDate.getMinutes(), 0);
          var scheduleDate = new Date($('#datetimepicker11')[0].value);
            
          if (scheduleDate.getTime() < currentDate.getTime()) {
            $scope.showAlert('Scheduled time should be greater that current time.');
            return
          }

          if (scheduleDate.getTime() - currentDate.getTime() < 1800000) {
            $scope.showAlert('Scheduled time should be at least 30 minutes.');
            return
          }
          messageBody['scheduleTime'] = scheduleDate;
        }

        /*** flag set for scheduleMsg***/
        if (messageBody.scheduledMsg) {
          messageBody.sent = false;
        } else {
          messageBody.sent = true;
        }

