      try {
        switch (channel) {
          case 'connect':
            $scope.editMessage = $('#connectMessage').val();
            break;
          case 'email':
            $scope.editMessage = $('#emailMessage').val();
            $scope.editTitle = $('#titleMessage').val();
            break;
          case 'sms':
            $scope.editMessage = $('#smsMessage').val();
            break;
        }
      } catch (e) {
        console.log(e);
      }

    };

    $scope.cancelContent = function(channel) {
      $scope.editMode = !$scope.editMode;
      if ($scope.editMessage == "~") {
        $scope.editMessage = $scope.description;
      }
      switch (channel) {
        case 'connect':
          $('#connectMessage').val($scope.editMessage);
          break;
        case 'email':
          $('#emailMessage').val($scope.editMessage);
          $('#titleMessage').val($scope.editTitle);
          break;
        case 'sms':
          $('#smsMessage').val($scope.editMessage);
          break;
      }
      $scope.init();
    };

    $scope.saveContent = function(channel) {

      switch (channel) {
        case 'connect':
          if (!$('#connectMessage').val()) {
            $scope.showAlert('Enter message for connect post.');
            return;
          }
          break;
        case 'email':
          if (!$('#emailMessage').val()) {
            $scope.showAlert('Enter message for email.');
            return;
          }
          if (!$('#titleMessage').val()) {
            $scope.showAlert('Enter title for email.');
            return;
          }
          $scope.title = $('#titleMessage').val();
          break;
        case 'sms':

          if (!$('#smsMessage').val()) {
            $scope.showAlert('Enter message for sms.');
            return;
          }

          break;
      }
      $scope.editMode = !$scope.editMode;

    };
    $scope.closeyoutube = function() {

      $scope.youtubeContainer = false;

    };
    $scope.scheduleListener = function() {
      if (!$scope.publishNow) {
        if (window.device) {
          var options = {
            date: new Date(),
            mode: 'dateTime',
            allowOldDates: false,
            minDate: ''
          };

