function pageprocessorbbMyCourses9Page5(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
	var data1 = $compile($(pagedef.datatemplate))($scope);


	$rootScope.productConfig = {
		product: pagedef.product,
		middlewareServerUrl: pagedef.middlewareServerUrl
	}

	$rootScope.loadBlocker = function() {
		$.blockUI({
			message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3 style="color:#fff;"></h3></div>'
		});
	};
	$rootScope.unloadBlocker = function() {
		$.unblockUI();
	};

	var tenant = "QueensCollege";

	$scope.loginError = false;

	if (!$rootScope.BB) {
		$rootScope.BB = {};
	}
	$rootScope.BB.loginEndpoint = $rootScope.productConfig.middlewareServerUrl + "/services/authenticate/" + tenant + "/" + $rootScope.productConfig.product.toUpperCase();
	$rootScope.BB.getEndpointURL = function(endpoint) {
		console.log("productconfig= ", $rootScope.productConfig);
		var serviceURL = $rootScope.productConfig.middlewareServerUrl + "/services/data/" + tenant + "/" + $rootScope.productConfig.product.toUpperCase() + "/" + endpoint + "?ticket=" + $rootScope.BB.ticket;
		return serviceURL;
	}
	$rootScope.BB.loginForm = function() {
		var loginData = {
			username: $scope.username,
			password: $scope.password
		};
		$rootScope.BB.login(loginData, function(err, data) {
			if (err) {
				$scope.loginError = true;
				$scope.loginErrorMessage = "Invalid Username or Password";
			} else {
				// navigate to MyCourses/bbMyCourses9
				$location.path("/app/bbMyCourses9/bbMyCourses9");
				$scope.$apply();
			}
		});
	};

	$rootScope.BB.login = function(data, callback) {
		console.log("login data ", data);
		var params = "username=" + encodeURIComponent(data.username) + "&password=" + encodeURIComponent(data.password);
		$rootScope.loadBlocker();
		$http.post($rootScope.BB.loginEndpoint, params).then(function(response) {
			console.log("response", response);
			$rootScope.unloadBlocker();
			if (response.data.error) {
				callback(response.error);
			} else {
				$rootScope.BB.ticket = response.data.ticket;
				$rootScope.BB.user = data;
				callback(undefined, data);
			}
		}, function(err) {
			$rootScope.unloadBlocker();

			callback(err);

		})
	};

	$rootScope.BB.postAPI = function(config, endpoint, postData, callback) {
		var serviceURL = $rootScope.BB.getEndpointURL(endpoint);
		console.log("serviceURL", serviceURL);
		$rootScope.loadBlocker();
		$http.post(serviceURL, postData).then(function(data, status) {
			console.log("response", data, "status", status);
			$rootScope.unloadBlocker();
			if (status == 403) {
				// ticker expired
				$rootScope.BB.login($rootScope.BB.user, function(err, data) {
					if (err) {
						//launch login screen
					} else {
						$rootScope.BB.postAPI(undefined, endpoint, postData, callback);
					}
				});
			} else {
				callback(undefined, data.data);
			}

		}, function(err) {
			console.log("Error", err);
			$rootScope.unloadBlocker();
			callback(err);

		});
	};


	$scope.bbLogin = function() {
		$rootScope.BB.loginForm();
	};

	$("#appContent").append(data1);


}

