jQuery.fn.extend({

    size: function() {

      return this.length;

    }

  });
  $scope.selIndex = $routeParams.index;
  /*  $scope.title = $rootScope.Eventsdata[$routeParams.index - 1].title;
    $scope.displaydate = $rootScope.Eventsdata[$routeParams.index - 1].displaydate;
    $scope.description = $rootScope.Eventsdata[$routeParams.index - 1].description;
    $scope.link = $rootScope.Eventsdata[$routeParams.index - 1].link;*/
  $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
  console.log($scope.selEvent);
  $scope.openurl = function() {
    window.open($scope.selEvent.link, "_blank");
  };

  var data = pagedef.pageTemplate;
  data = $(data).append($(pagedef.datatemplate));
  console.log(JSON.stringify(data));
  var data1 = $compile($(data))($scope);
  $("#appContent").append(data1);

  $scope.trustHtml = function(data) {
    if (!data) {
      return data;
    }

    data = data.replace(/(?:\\r\\n|\\r|\\n)/g, '<br/>');
    data = data.replace(/(?:\\)/g, '');
    return $sce.trustAsHtml(data);
  }

  $scope.openFeed = function() {
    $location.path("/app/Events/Events");
    return;
    /*    $rootScope.Events = $scope.index;
        $rootScope.index = index;
        $scope.Eventsdata = $rootScope.Events.Eventsdata;
        console.log($scope.Eventsdata);
        $location.path("/app/Events/Events");*/
  };

  $scope.next = function() {
    var len = $rootScope.Eventsdata.length;
    console.log(len);
    try {
      console.log($scope.selIndex);
      if ($scope.selIndex < (len - 1)) {
        $scope.selIndex++;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }
      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };

  $scope.prev = function() {
    try {
      console.log($scope.selIndex);

      if ($scope.selIndex > 0) {
        $scope.selIndex--;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }

      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
}

function pageprocessorClassSchedule46(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location, $timeout) {
  // IE fix for endWith function
  if (!String.prototype.endsWith) {
    String.prototype.endsWith = function(search, this_len) {
      if (this_len === undefined || this_len > this.length) {
        this_len = this.length;
      }
      return this.substring(this_len - search.length, this_len) === search;
    };
  }
  var config = {
    "mapApi": pagedef.apikey,

  }
  var licenseKey = pagedef.licensekey;
  //  console.log(" config.mapApi " + config.mapApi);
  //$rootScope.sourceLocation='';
  $rootScope.latlngLoad = '';
  var errorCallback = function(error) {
    $.unblockUI();
    //  console.log("ERROR" + error.code + "MSG" + error.message);
    switch (error.code) {
      case 0:
        {
          navigator.notification.alert("Unable to locate your location.", function() {}, 'Alert', 'Ok');
          break;
        }
      case 1:
        {
          navigator.notification.alert("Your device GPS is not enabled. Please enable it to get direction to locations.", function() {}, 'Alert', 'Ok');
          break;
        }
      case 2:
        {
          navigator.notification.alert("Oops! Your Position is not available.", function() {}, 'Alert', 'Ok');
          break;
        }
    }
  }

  var placeGeoCoder = function(results) {
    setTimeout(function() {
        try {
          $.unblockUI();

          placeCoordinate = results;
          $rootScope.placeCoordinate = placeCoordinate;
          geocoder = new google.maps.Geocoder();
          var mapDisplay = document.getElementById('placeMapCanvas');
          console.log("var mapDisplay" + mapDisplay);
          //console.log("mmmmmmapDisplay"+$('document #placeMapCanvas'))
          var latlng = new google.maps.LatLng(results.coords.latitude, results.coords.longitude);
          $rootScope.latlngLoad = latlng;
          console.log("latlngfff" + latlng)
            //  $rootScope.destinationLocation = new google.maps.LatLng($rootScope.destinationLocationLat, $rootScope.destinationLocationLong);

          $rootScope.sourceLocation = latlng;
          $rootScope.sourceLocationLat = results.coords.latitude;
          $rootScope.sourceLocationLong = results.coords.longitude;
          var myOptions = {
            zoom: 8,
            center: latlng,
            mapTypeId: google.maps.MapTypeId.ROADMAP
          };

          map = new google.maps.Map(mapDisplay, myOptions);


          geocoder.geocode({
            'latLng': latlng
          }, function(results, status) {
            if (status == google.maps.GeocoderStatus.OK) {
              if (results[0]) {

                // console.log(JSON.stringify(results[0]));
                // $("#currentUserLoc").html(results[0].formatted_address);
              } else {
                console.log('No results found');
              }
            } else {
              alert('Geocoder failed due to: ' + status);
            }
          });
        } catch (e) {
          $.unblockUI();
          // alert("Error : " + e);
        }
      }, 200)
      //pageChange("places");
  }



  var placescb = function() {
    if (navigator.geolocation) {
      // console.log("navigator" + JSON.stringify(navigator.geolocation))
      $.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
      });
      navigator.geolocation.getCurrentPosition(placeGeoCoder, errorCallback, {
        timeout: 50000,
        frequency: 3000,
        enableHighAccuracy: true
      });
    } else {
      alert("Geolocation not working")
    }
  }

  angular.element(document).ready(function() {
    if (true) {
      return;
    }
    window.placescb = placescb;

    var ref = window.document.getElementsByTagName('script')[0];

    // Create a new script element
    var script = window.document.createElement('script');

    // Set the script element `src`
    // script.src = "https://maps.googleapis.com/maps/api/js?key=" + config.mapApi + "&sensor=false&callback=placescb";

    script.src = "https://maps.googleapis.com/maps/api/js?key=" + config.mapApi + "&sensor=false&callback=placescb";

    // Inject the script into the DOM
    var len = $('script').filter(function() {
      return ($(this).attr('src') == script.src);
    }).length;

    console.log("LEEENNNNNNNNN" + len);
    if (len == 0) {
      ref.parentNode.insertBefore(script, ref);
      var timer = setInterval(function() {
        console.log('TIMER');
        if (typeof google === 'object' && typeof google.maps === 'object') {
          placescb()
          console.log("LOADED....");
          clearInterval(timer)
        } else {
