  var counter = 0;
  /****************************************************************************************************************************************/

  $rootScope.getCanvasRefreshToken = function(callBack) {
    $.blockUI();

    console.log("getrefresstoken.......");
    var endpoint = "/login/oauth2/token";
    var url = canvasBaseUrl + endpoint;
    if (endpoint.indexOf(canvasBaseUrl) != -1) {
      url = endpoint;
    }
  //  var authpostdata = "grant_type=refresh_token&client_id=" + clientid + "&client_secret=" + clientsecret + "&refresh_token=" + $rootScope.canvasrefreshtoken;
    config.postdata = {
      "authService": "canvasrefreshtoken",
      "url": url,
      "method": "POST",
      "access_token": $rootScope.canvasaccesstoken,
      "postData": "",
      "host": canvasBaseUrl,
      "client_id": clientid,
      "client_secret": clientsecret,
      "refresh_token":$rootScope.canvasrefreshtoken,
      "tenant": tenantId,
      "licenseKey": licenseKey
    }

    config.contentType = "application/json";

    var counter = 0;
    $rootScope.getErpDataAPI(config, url, function(data) {
      if (data && data.response && typeof data.response == "string") {
        data.response = JSON.parse(data.response);
        console.log("responsegetrefreestoken1", data.response)
      }

      if (data && data.data && typeof data.data.data == "string") {
        data.response = JSON.parse(data.data.data);
        console.log("responsegetrefreestoken2", data.response)
      }
      console.log("refresh token:");
      $rootScope.canvasaccesstoken = data.response.access_token;
      console.log("$rootScope.canvasaccesstoken=" + $rootScope.canvasaccesstoken);
      if(data){
      return callBack(data);
      }else{
      return callBack("Some error occured");
      }

    })
  }


  $rootScope.invokeCanvasAPI = function(endpoint, method, postdata, callback) {
    $.blockUI();
    console.log("invokeCanvasApi........", counter)
    var url = canvasBaseUrl + endpoint;
    if (endpoint.indexOf(canvasBaseUrl) != -1) {
      url = endpoint;
    }
    config.postdata = {
      "authService": "canvasserviceadapter",
      "url": url,
      "method": method,
      "access_token": $rootScope.canvasaccesstoken,
      "postData": "",
      "tenant": tenantId,
      "licenseKey": licenseKey
    }
    config.contentType = "application/json";

    var counter = 0;
    $rootScope.getErpDataAPI(config, url, function(data, status, headers, config) {
    //  console.log(data);
    
      if (data && data.response && typeof data.response == "string") {
        data.response = JSON.parse(data.response);
        console.log("response1", data.response)
        $.unblockUI();
        return callback(data);
      }else if(data && data.data && typeof data.data == "string") {
        data.response = JSON.parse(data.data);
        console.log("response2", data.response)
        $.unblockUI();
        return callback(data);
      } else if(data && data.message == "401 Unauthorized") {
        counter++;
        if (counter > 2) {
          $.unblockUI();
          return callback(data);
        } else {
          counter++;
          $rootScope.getCanvasRefreshToken(function(data, status, headers, config) {
            if(data){
              $rootScope.invokeCanvasAPI(endpoint, method, postdata, callback);
            }else{
              alert("Some issue occured.Please try again...");
           }
          });
        }
      } else {
         $.unblockUI();
        alert("Idp session is expired.Please login again...");
      //  $location.path("/logout");
        
      }
    });

  }

