function pageprocessorCanvasCoursesPage12(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  console.log("in assignments page...");
  $scope.isDirect = false;

  $scope.canvasSelCourseId = $rootScope.canvasSelCourse.id;

  var getCourse = function(id) {
    var course = null;
    $.each($scope.canvasmycourses, function(key, val) {
      if (id == val.id) {
        course = val;
      }
    });
    return course;
  }

  $scope.dropdownselect = function(id) {
    $rootScope.canvasSelCourse = getCourse($scope.canvasSelCourseId);
    $scope.showOverdue();
  }

  $scope.trustHtml = function(html) {
    return $sce.trustAsHtml(html);
  }

  $scope.odflag = false;
  $scope.upflag = false;
  $scope.unflag = false;
  $scope.paflag = false;

  $scope.showOverdue = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=overdue", "GET", {}, function(assignments) {
      $scope.canvasSelCourceODAssn = assignments.response;
      //  alert(JSON.stringify($scope.canvasSelCourceODAssn) )
      if ($scope.canvasSelCourceODAssn && $scope.canvasSelCourceODAssn != null && $scope.canvasSelCourceODAssn.length > 0) {
        $scope.odflag = true;
      }
    });

    // $scope.upflag = false;
    //  $scope.unflag = false;
    //  $scope.paflag = false;
  }

  $scope.showUpcoming = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=upcoming", "GET", {}, function(assignments) {
      $scope.canvasSelCourceUPAssn = assignments.response;
      //   alert(JSON.stringify($scope.canvasSelCourceUPAssn))
      if ($scope.canvasSelCourceUPAssn && $scope.canvasSelCourceUPAssn != null && $scope.canvasSelCourceUPAssn.length > 0) {
        $scope.upflag = true;
      }
    });
    // $scope.odflag = false;

    // $scope.unflag = false;
    //// $scope.paflag = false;
  }

  $scope.showUndated = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=undated", "GET", {}, function(assignments) {
      $scope.canvasSelCourceUNAssn = assignments.response;
      // alert(JSON.stringify($scope.canvasSelCourceUNAssn))
      if ($scope.canvasSelCourceUNAssn && $scope.canvasSelCourceUNAssn != null && $scope.canvasSelCourceUNAssn.length > 0) {
        $scope.unflag = true;
      }
    });
    //   $scope.odflag = false;
    // $scope.upflag = false;
    //  $scope.paflag = false;
  }

  $scope.showPast = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=past", "GET", {}, function(assignments) {
      $scope.canvasSelCourcePAAssn = assignments.response;
      // alert(JSON.stringify($scope.canvasSelCourcePAAssn))
      // $scope.odflag = false;
      // $scope.upflag = false;
      // $scope.unflag = false;
      if ($scope.canvasSelCourcePAAssn && $scope.canvasSelCourcePAAssn != null && $scope.canvasSelCourcePAAssn.length > 0) {
        $scope.paflag = true;
      }
    });
  }

  $scope.openAssignment = function(item) {
    if (item.quiz_id) {
      $rootScope.quizItem = item;
      $location.path("/app/CanvasQuizzes/CanvasQuizzesPage2");
    } else if (item.discussion_topic) {
      $rootScope.discussion_id = item.discussion_topic.id;
      $rootScope.canvasSelDiscussion = item.discussion_topic;
      $location.path("/app/CanvasDiscussions/CanvasDiscussionsPage2");
    } else {
      $rootScope.assigmentItem = item;
      $location.path("/app/CanvasAssignments/CanvasAssignmentsPage4");
    }
  };


  $scope.openCourse = function(course) {
    $rootScope.canvasSelCourse = course;
    $location.path("/app/CanvasAssignments/CanvasAssignmentsPage2");
  }


  $scope.checkAvailability = function(date) {
    if (date == null) {
      return
    }
    var lockDate = new Date(date);
    var d = new Date();
    if (lockDate > d) {
      var a = lockDate.toString();
      return "Available until: " + a.substr(4, 6);
    } else {
      return "Closed"
    }
  }

  $scope.dueDate = function(date) {
    if (date == null) {
      return
    }
    var d = new Date(date);
    var hours = d.getHours();
    var minutes = d.getMinutes();
    var ampm = hours >= 12 ? 'pm' : 'am';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? '0' + minutes : minutes;
    var strTime = hours + ':' + minutes + ' ' + ampm;
    d = d.toString();
    //alert(d)
    return d.substr(4, 6) + " at " + strTime;
  }


  $scope.init = function() {
    $scope.showOverdue();
    $scope.showPast();
    $scope.showUndated();
    $scope.showUpcoming();

  }

  $scope.init();

}

