    $scope.updateProfileData = function () {
        if(window.device && window.plugins.crop){
            var img = document.getElementById('profileImg');
            if(img != null){
            var c = document.createElement('canvas');
            c.height = img.height;
            c.width = img.width;
            var ctx = c.getContext('2d');
            ctx.drawImage(img, 0, 0, c.width, c.height);
            $scope.newProfileImage = c.toDataURL();
            }
        }
        //console.log($scope.newProfileImage , "Selected Profile Background:");

        if ($scope.newProfileImage.length != 0) {
            $scope.currentUser.profileImage = $scope.newProfileImage;
            $scope.currentUser.profileBackground = $scope.currentUser.profileBackground;
            $scope.isDirty = true;
        }

        var pref = [];
        for (var i = 0; i < $scope.areaOfInterest.length; i++) {
            if ($scope.areaOfInterest[i].selected && $scope.areaOfInterest[i].enable == true)
                pref.push($scope.areaOfInterest[i].id);
        }

        // console.log("$scope.areaOfInterest.length", $scope.areaOfInterest);
        // console.log("pref.length", pref);
        // console.log("pref Count", pref.length);
        // console.log("$scope.allCategoryEnbleStatus", $scope.allCategoryEnbleStatus);

        if (pref.length == 0) {
            var msg = "Please select atleast one preference";
            showAlert(msg);
            return;
        }

        // if (pref.length == 0 && $scope.allCategoryEnbleStatus) {
        //     var msg = "Please select atleast one preference";
        //     showAlert(msg);
        //     return;
        // }

        //console.log(pref);
        $scope.currentUser.areaOfInterest = pref;
        $rootScope.user['areaOfInterest'] = pref;
        if (!$scope.isDirty) {
            return;
        }
        //Saving
        console.log("Updating Profile Data...");
        try {
            //console.log($scope.newProfileImage.length);

            if ($scope.newProfileImage.length != 0) {
                $.blockUI();
                var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/imageUpload";
                var data = {
                    'imageData': $scope.newProfileImage,
                    "imageExtn": 'png'
                };
