    $scope.uploadProfileImage = function () {
        if (window.device) {
            $scope.openCamera();
        } else {
            try {
                console.log('testing else');
                $("#uploadProfileImg").change(function () {
                    if (this.files && this.files[0]) {

                        if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {

                            var FR = new FileReader();

                            FR.onload = function (e) {

                                $scope.targetImage = e.target.result;

                                //Set image width before cropping (modal-image)
                                $scope.getmodalCropUrl = $scope.targetImage;
                                console.log('modal image before crop ', $scope.getmodalCropUrl);
                                var winWidth = $(window).width();

                                if (winWidth <= 549) {
                                    $("#profileCropModal .cropModalContent").width(winWidth - 13);
                                }

                                var Readerimg = new Image();
                                Readerimg.src = e.target.result;

                                Readerimg.onload = function () {
                                    $scope.ReaderimgWid = this.width;
                                    $scope.ReaderimgHeight = this.height;

                                    if ($scope.ReaderimgWid <= 200 && $scope.ReaderimgHeight <= 200) {
                                        alert('Please Use Bigger Image.');
                                        return false;
                                    }

                                    if ($scope.ReaderimgWid < 550) {

                                        $scope.ModalCropImage = "";

                                        $("#profileCropModal").modal("show");
                                        $scope.ModalCropImage = e.target.result;

                                        (function bindDrag() {
                                            var container = $(".ModalcropBody");
                                            $("#canvas").resizable({
                                                aspectRatio: true,
                                                handles: 'n,s,e,w,ne,se,nw,sw',
                                                minWidth: 115,
                                                minHeight: 115,
                                                maxWidth: $scope.pixelsWid,
                                                maxHeight: $scope.pixelsHeight,
                                                containment: container
                                            }).draggable({
                                                containment: container,
                                                scroll: false
                                            });

                                        })();


                                        $('#canvas').css('width', 120).css('height', 120);
                                        $scope.$digest();
                                    } else {
                                        $scope.addCropModal();
                                    }

                                }
                            };

                            if (this.files[0].size > 7000000) {

                                if (window.device) {
                                    navigator.notification.alert("Image size is too large. Please Upload below 7MB");
                                } else {
                                    alert("Image size is too large. Please Upload below 7MB");
                                }
                                return;
                            }

                            FR.readAsDataURL(this.files[0]);
                            $("#uploadProfileImg")[0].value = '';
                        } else {
                            if (window.device) {
                                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
                            } else {
                                alert("Only png, jpg file types are allowed.");
                            }
                        }
                    }
                });

                document.getElementById("uploadProfileImg").click();
            } catch (e) {
                alert(e);
            }

        }

    };




