        $scope.decreaseValue = $scope.ReaderimgWid - getModalWidth;
        $scope.getPerDecresceVal = ($scope.decreaseValue / $scope.ReaderimgWid) * 100;
        $scope.pixelsWid = $scope.ReaderimgWid - $scope.ReaderimgWid * ($scope.getPerDecresceVal / 100);
        $scope.pixelsHeight = $scope.ReaderimgHeight - $scope.ReaderimgHeight * ($scope.getPerDecresceVal / 100);
        var convertdCanvasWidth = $scope.pixelsWid;
        var convertdCanvasHeight = $scope.pixelsHeight;
        var image = new Image();
        image.src = $scope.targetImage;
        image.onload = function () {
            image.src = $scope.targetImage;
            width = image.width;
            height = image.height;
            var convertCanvas = document.getElementById("modalCanvas");
            var ctx = convertCanvas.getContext("2d");


            if ($(window).width() <= 768) {
                var getModalWidth = $(window).width();
                var popupWidth = getModalWidth - 15;
            } else {
                var getModalWidth = $("#profileCropModal .cropModalContent").width();
                var popupWidth = getModalWidth - 32;
            }
            if (width > height) {
                if (width > popupWidth) {
                    height *= popupWidth / width;
                    width = popupWidth;
                }
            } else {
                if (height > popupWidth) {
                    width *= popupWidth / height;
                    height = popupWidth;
                }
            }
            convertCanvas.width = width;
            convertCanvas.height = height;
            $scope.convertdCanvasWidth = width;
            $scope.convertdCanvasHeight = height;
            if (window.device) {
                $('.cropModalContent').width(width);
            }
            ctx.drawImage(image, 0, 0, $scope.convertdCanvasWidth, $scope.convertdCanvasHeight);
            fitToContainer();
            image.src = $scope.targetImage;
            $scope.ModalCropImage = convertCanvas.toDataURL();
            $scope.$apply();
        }

