        "id": "all",
        "category": "All",
        "enable": true,
        "selected": false
      });

      if ($rootScope.areaOfInterest) {
        $scope.areaOfInterest = $rootScope.areaOfInterest;
      } else {
        $scope.areaOfInterest = data['_embedded']['category'];
