function pageprocessorConnectPage8(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $scope.category = $rootScope.category[$rootScope.categoryIndex];
  $scope.category.whatsupAdmins = [
    'zack_morris@seark.edu',
    'andrew_mathur@seark.edu'
  ];
  $scope.editMode = false;
  $scope.categoryLabel = $scope.category.category;
  $rootScope.appDisplayName = "Categories";
  $scope.email = '';
  $scope.isValid = false;
  var testMail = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;


  var flag = true;

  for (var i = 0; i < $rootScope.category.length; i++) {
    if ($rootScope.category[i].enable == true || $rootScope.category[i].rename == false) {
      flag = false;
      break;
    }
  }

  var url = '/unifyedcategory/category';

  $scope.$destroy = function() {
    $rootScope.category[$rootScope.categoryIndex] = $scope.category;
  };

  $scope.update = function() {
    var categoryUrl = url + '/' + $scope.category['id'];

    $rootScope.callConnectAPI(categoryUrl, 'PUT', $scope.category, function(res) {});

  };

  $scope.changeState = function() {


    if (flag && $scope.category.enable) {
      if (window.device) {
        navigator.notification.confirm(
          "Category name can only be changed prior to being enabled. Once enabled, a category name can't be changed.", // message
          onConfirm, // callback to invoke with index of button pressed
          '', // title
          'Ok,Cancel' // buttonLabels
        );
      } else if ($rootScope.webApp) {

        $.confirm({
          animation: 'none',
          icon: 'fa fa-trash fa-2x',
          title: '',
          content: "Category name can only be changed prior to being enabled. Once enabled, a category name can't be changed.",
          titleClass: 'proximaSemiBoldFont fontSize18 textcenter',
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            CANCEL: {
              'btnClass': 'btn-cancel boxradius popBtns',
              'action': function() {
                //alert('cancel');
                $scope.category.enable = !$scope.category.enable;
                $scope.$apply();
              }
            },
            OK: {
              'btnClass': 'btn-primary boxradius marginleft30px popBtns',
              'action': function() {

                $scope.category.rename = false;
                $scope.update();

              }
            }
          }
        });
      } else {
        var r = confirm("Category name can only be changed prior to being enabled. Once enabled, a category name can't be changed.");
        if (r == true) {
          $scope.category.rename = false;
          $scope.update();
        } else {
          $scope.category.enable = !$scope.category.enable;
          $scope.$apply();
        }
      }

    } else {
      $scope.category.rename = false;
      $scope.update();
    }


    function onConfirm(buttonIndex) {
      if (buttonIndex == 1) {
        $scope.category.category = $scope.categoryLabel;
        $scope.update();
      } else {
        $scope.category.enable = !$scope.category.enable;
        $scope.$apply();
      }
    }


  };

  $scope.toggleView = function() {
    if ($scope.category['rename'] && !$scope.category['enable']) {
      $scope.editMode = !$scope.editMode;
      $scope.categoryLabel = $scope.category.category;
    }
  };
  $('#categoryRenameForm').on('submit', function(event) {
    event.preventDefault();

    $scope.$digest();

    $scope.category.category = $scope.categoryLabel;
    $scope.update();
    $scope.editMode = !$scope.editMode;

    return false;
  });

  $scope.emailChange = function() {
    $scope.isValid = testMail.test($scope.email);
    console.log($scope.isValid);
  }


}

function pageprocessorTermsCondition(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  setTimeout(function() {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    //  alert($(window).height());
    var height = $(window).height() - $(".navbar-absolute-top").height() - $("#btnAgree").height() - $(".appbodycontent").height() - 60;
    $("#scroll").css('height', height);


    $scope.height = function() {
      $("#appContent").html('');
      var data1 = $compile($(pagedef.datatemplate))($scope);
      $("#appContent").append(data1);
      var height = $(window).height() - $(".navbar-absolute-top").height() - $("#btnAgree").height() - $(".appbodycontent").height() - 60;
      $("#scroll").css('height', height);

    }

    var handleEmergencyOrientation = function() {
      if (orientation == 0) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else if (orientation == 90) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else if (orientation == -90) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else if (orientation == 180) {
        setTimeout(function() {
          $scope.height();
        }, 100);
      } else {}
    }
    window.handleOrientation = handleEmergencyOrientation;
    window.addEventListener('orientationchange', handleOrientation, false);

    $scope.$on('$destroy', function() {
      window.removeEventListener('orientationchange', handleEmergencyOrientation, false);
      window.handleOrientation = null;
    });


    $scope.terms = function() {
      $.jStorage.set('termsCondition', true);
      $location.path("/app/BlueLightEmergency/BlueLightEmergency");

    }


  }, 10);

}

function pageprocessorAlertus(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var plugins = cordova.require("cordova/plugin_list").metadata;

  if (typeof plugins['com.venkykowshik.startapp'] === "undefined") {

    navigator.notification.alert("Please update your app to use this feature", function() {
      window.location.href = "index.html#home";
      $.unblockUI();
      //$location.path("/home");
    }, "Update required", "OK");
  } else {
    if (window.device.platform == "iOS") {
      var iosUri = pagedef.iosuri;
      navigator.startApp.check(iosUri, function(message) { /* success */
          navigator.startApp.start(iosUri, function(message) { /* success */
              $location.path("/home");
              $.unblockUI();
            },
            function(error) { /* error */
              console.log(error);
            });
        },
        function(error) { /* error */
          $scope.loadurl(pagedef.itunesurl);
          $location.path("/home");
          $.unblockUI();
        });
    } else if (window.device.platform == "Android") {
      var androidpackage = pagedef.androidpackage
      navigator.startApp.check(androidpackage, function(message) { /* success */
          navigator.startApp.start(androidpackage, function(message) { /* success */
              $location.path("/home");
              $.unblockUI();
            },
            function(error) { /* error */
              navigator.notification.alert("Your device isn't compatible with this version");
              $location.path("/home");
              $.unblockUI();
            });
        },
        function(error) { /* error */
          $scope.loadurl(pagedef.playstoreurl);
          $location.path("/home");
          $.unblockUI();
        });
    } else {
      apprise("This feature is not available on Emulator", {
        "verify": false,
        "textYes": "Ok"
      }, function(r) {});
    }


    $scope.loadurl = function(url) {
      if (window.device) {
        $.blockUI();
      }
      var iabRef = window.open(url, device.platform == "Android" ? "_system" : "_blank", "location=no,hidden=yes");
      var hideBlockUi = function() {
        iabRef.show();
        $.unblockUI();
      };
      var iabClose = function(data) {
        window.location.href = "index.html#home";
        $.unblockUI();
      };
      iabRef.addEventListener("exit", iabClose);
      var loadStop = function(data) {
        hideBlockUi();
      };
      iabRef.addEventListener("loadstop", loadStop);
    }
  }
}

