    var placesapikey = "AIzaSyABYcg8dh0PuvHuGygJchQVSgca2UxMyws";

    $scope.PlacesSuccessCallback = function(placeType) {
        try {
             $.blockUI();
            var latitudeVal = placeCoordinate.coords.latitude;
            var longitudeVal = placeCoordinate.coords.longitude;
            var distance = $("#distance").val();
            var place_url = "https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=" +
                $rootScope.placeCoordinate.coords.latitude + "," + $rootScope.placeCoordinate.coords.longitude + "&radius=20000&types=" + placeType +
                "&sensor=true&key=" + placesapikey;
            console.log(place_url);
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = place_url;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(place_url);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $.ajax({
                url: url,
                method: proxyMethod,
                data: proxyData,
                success: function(data) {
          $rootScope.appDisplayName = placeType;
                    //$(".placeHeader").html(placeType + "S");
                    //displayPlaces(data)
                    //alert ("Data : " + JSON.stringify(data));
                    console.log(JSON.stringify(data));
                    $rootScope.placesdata = data;
                    $rootScope.$apply(function() {
                        $location.search("placetype",placeType).path("/app/Places2/Places2Page2");
                        $.unblockUI();
                    });
                },
                error: function(e) {
                    $.unblockUI();
                    //alertServerError(e.status);
                    alert("Error : " + e);
                }

            });

        } catch (e) {
            alert(e)
        }

    }

    var geocoder;
    var placeCoordinate;
    var errorCallback = function(error) {
        $.unblockUI();
        switch (error.code) {
            case 3:
                    {
                        var errmsg = "Could not get the current position. Either GPS signals are weak or GPS has been switched off.";
                        break;
                    }
                case 1:
                    {
                        var errmsg = "Either GPS signals are weak or GPS has been switched off.";
                        break;
                    }
                case 2:
                    {
                        var errmsg = "Unable to get your current position.";
                        break;
                    }
            }
            if (window.device) {
                navigator.notification.alert(errmsg, null, 'GPS Error', 'Ok');
                $location.path("/app/BlueLightEmergency/BlueLightEmergency");
            } else {
                alert(errmsg);
            }
    }
    var placeGeoCoder = function(results) {
        try {
            $.unblockUI();
            var data1 = $compile($(pagedef.datatemplate))($scope);
            $("#appContent").append(data1);

            //alert("place geo coder : " + JSON.stringify(results));
            console.log(JSON.stringify(results));
            placeCoordinate = results;
            $rootScope.placeCoordinate = placeCoordinate;
            geocoder = new google.maps.Geocoder();
            var latlng = new google.maps.LatLng(results.coords.latitude, results.coords.longitude);
            geocoder.geocode({
                'latLng': latlng
            }, function(results, status) {
                //alert("Result1 : " + JSON.stringify(results));
                //console.log(JSON.stringify(results));
                if (status == google.maps.GeocoderStatus.OK) {
                    if (results[0]) {
                        //alert("Result : " + JSON.stringify(results[0]));
                        //alert("formatted result : " + results[0].formatted_address);
                        console.log(JSON.stringify(results[0]));
                        $("#currentUserLoc").html(results[0].formatted_address);
                    } else {
                        alert('No results found');
                    }
                } else {
                    alert('Geocoder failed due to: ' + status);
                }
            });
        } catch (e) {
            $.unblockUI();
            alert("Error : " + e);
        }
        //pageChange("places");
    }

    //if (window.device) {
    var placescb = function() {
        if (navigator.geolocation) {
            $.blockUI();
            navigator.geolocation.getCurrentPosition(placeGeoCoder, errorCallback, {
                timeout: 10000,
                frequency: 3000,
                enableHighAccuracy: true
            });
        } else {
            alert("not working")
        }
    }
    window.placescb = placescb;
    $.getScript("https://maps.googleapis.com/maps/api/js?key=" + mapapikey + "&sensor=false&callback=placescb");
    //}
}

