    $("#MapMapCanvas").css("height", mapheight + "px");



    $scope.CheckUserStatsus = function(index, userPhn) {
        setTimeout(function() {
            var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
            var proxyMethod = "POST";
            var qdata = {
                "UserPhn": userPhn,
            };
            var proxyData = qdata;
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(result, status, headers, config) {
                if (result.length == 0) {
                    $scope.users[index].active = false;
                } else {
                    var status = result[result.length - 1].active;
                    if (status == 'Yes') {
                        //data[i].active=true; 
                        $scope.users[index].active = true;
                    } else {
                        $scope.users[index].active = false;
                    }

                }
                //alert(JSON.stringify($scope.users));
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
            });
        }, 2000);

    }

    var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=GuardiansList";
