        for (var i = 0; i < $scope.users.length; i++) {
            $scope.CheckUserStatsus(i, $scope.users[i].UserPhn);
        }
        }
    }).
    error(function(data, status, headers, config) {
        $.unblockUI();
    });

    $scope.CheckTrackingStatus = function(userPhn) {
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
        var proxyMethod = "POST";

        var data = {
            "UserPhn": userPhn,
        };
        var proxyData = data;
        $http({
            method: proxyMethod,
            url: url,
            data: proxyData
        }).
        success(function(data, status, headers, config) {
            alert(JSON.stringify(data));
            var status = "Tracking Status: " + data[data.length - 1].active;
            return status;
        }).
        error(function(data, status, headers, config) {
            $.unblockUI();
        });
    }

    $scope.getUserTrackingDetails = function(trackID) {
        var userPhn = userInfo.UserPhn;
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackMe";
        var proxyMethod = "POST";

        var data = {
            "userPhn": userPhn,
            "Trackingid": trackID
        };
        var proxyData = data;
        $http({
            method: proxyMethod,
            url: url,
            data: proxyData
        }).
        success(function(data, status, headers, config) {
                try{var lat = data[data.length - 1].Lat;
                var lng = data[data.length - 1].Long;
                var latlng = new google.maps.LatLng(lat, lng);
                var myOptions = {
                    zoom: 14,
                    center: latlng,
                    mapTypeId: google.maps.MapTypeId.ROADMAP
                };
                map = new google.maps.Map(mapDisplay, myOptions);
                map.addMarker($scope.createMarker(data[data.length - 1].User+ ' current location', latlng));
            }catch(e){}
                }).error(function(data, status, headers, config) {
                $.unblockUI();
            });
        }

        $scope.createMarker = function(text, latlng) {
            var marker = new google.maps.Marker({
                position: latlng,
                map: map
            });
            infowindow = new google.maps.InfoWindow({
                content: text
            });
            infowindow.open(map, marker);
            return marker;
        }

        $scope.trackUserInterval = function() {
            var userPhn = userInfo.UserPhn;
            var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
            var proxyMethod = "POST";

            var data = {
                "UserPhn": userPhn,
            };
            var proxyData = data;
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                if (data[data.length - 1].active == 'No') {
                    navigator.notification.alert('Tracking stopped by user', null, 'Alert', 'Ok');
                    $scope.GuardianTracking=false;
                    clearInterval(usertracking);
                    usertracking = false;
                } else {
                    var trackID = data[data.length - 1].trackingId;
                    $scope.getUserTrackingDetails(trackID);
                }
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
            });
        }

        var usertracking = false;
        var gudata;
        $scope.trackUser = function(userData) {
            if (!usertracking) {
                $scope.GuardianTracking=false;
                usertracking = true;
                userInfo = userData;
                $scope.uName=userInfo.userName;
                $scope.UpdateGuardianStatus();
            }
        }

        $scope.UpdateGuardianStatus = function() {
            var userPhn = userInfo.UserPhn;
            var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
            var proxyMethod = "POST";

            var data = {
                "UserPhn": userPhn,
            };
            var proxyData = data;
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                if (data.length == 0) {
                    navigator.notification.alert("The user is not tracking.", null, 'Alert', 'Ok');
                    usertracking = false;
                } else {
                    if (data[data.length - 1].active == 'No') {
                        navigator.notification.alert("The user is not tracking.", null, 'Alert', 'Ok');
                        usertracking = false;
                    } else {
                        $scope.trackUserInterval();
                        var userPhn = userInfo.UserPhn;
                        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
                        var proxyMethod = "POST";

                        var data = {
                            "UserPhn": userPhn,
                        };
                        var proxyData = data;
                        $http({
                            method: proxyMethod,
                            url: url,
                            data: proxyData
                        }).
                        success(function(data, status, headers, config) {
                            var trackID = data[data.length - 1].trackingId;
                            var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/createTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=ActiveGuardians";
                            var proxyMethod = "POST";
                            gudata = {
                                "userPhn": userPhn,
                                "trackingid": trackID,
                                "userName": userInfo.userName,
                                "guardianPhn": userInfo.GuardianPhn,
                                "guardianName": userInfo.guardianName,
                                "GuardianTracking": "Yes"
                            };
                            var proxyData = gudata;
                            $http({
                                method: proxyMethod,
                                url: url,
                                data: proxyData
                            }).
                            success(function(data, status, headers, config) {
                                //alert(JSON.stringify(data));
                                gudata._id=data._id;
                            }).error(function(data, status, headers, config) {
                                $.unblockUI();
                            });

                        }).
                        error(function(data, status, headers, config) {
                            $.unblockUI();
                        });

                        usertracking = setInterval($scope.trackUserInterval, 10000);
                    }
                }
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
            });
        }
    }catch(e){alert(e)}
    }

