        $scope.dropdown = data;
        if ($scope.dropdown.transcripttypedropdown.length > 0) {
          $scope.creditselectedvalue = $scope.dropdown.transcripttypedropdown[2].TRANSCRIPT_DESC;
          $('#credittype').blur();
