authFunction:
 function authFunction(uname, password, $http, $rootScope, success, error) {
   var authSuccess = success;
   var authError = error;

   $rootScope.loginToPortal = function() {
     $.blockUI();
     var exitCallBack = function(data) {
       $.unblockUI();
     };
     var hideBlockUi = function() {
       $.unblockUI();
       inAppBrowserRef.close();
     };
     var cb = function(data) {
       //alert('cb');
     };
     var messageCallBack = function(params) {
       console.log(params.data);
       if (params.data.error) {
         hideBlockUi();
         navigator.notification.alert('Your UT EID or password was incorrect.');
         authSuccess({
           'error': 'Your UT EID or password was incorrect.'
         });
         return;
       } else if (params.data.success) {
         hideBlockUi();
         code = JSON.parse(params.data.success);
         console.log(code);
         $rootScope.loggedIn = true;
         $rootScope.ticket = code.accessToken;
         authSuccess({
           "ticket": code.accessToken,
           "roles": code.role,
           "authdata": code
         });
       } else {
         inAppBrowserRef.show();
       }
     }

     var loadStartCallBack = function(data) {
       console.log('loadStartCallBack', data);
       //alert("data.url=============" + data.url);
       if (data.url.indexOf('uta-staging.unifyed.com/auth') == -1 && data.url.indexOf('uta-staging.unifyed.com') != -1 && data.url.indexOf('uta-staging.unifyed.com/userinfo') == -1) {
         //alert("start data.url" + data.url);
         var cd = "window.location = 'https://uta-staging.unifyed.com/userinfo'";
         inAppBrowserRef.executeScript({
           code: cd
         }, null);
       }
     }
     var loadStopCallBack = function(data) {
       console.log('loadStopCallBack', data);
       if (data.url.indexOf('s1') != -1) {
         //alert("data.url" + data.url);
         var cd = "document.getElementById('username').value ='" + uname + "';document.getElementById('password').value = '" + password + "';document.getElementsByName('_eventId_proceed')[0].click();";
         inAppBrowserRef.executeScript({
           code: cd
         }, null);
       }
       if (data.url.indexOf('s2') != -1) {
         var cd = "try{webkit.messageHandlers.cordova_iab.postMessage(JSON.stringify({error:document.getElementsByClassName('form-error').length}));}catch(e){alert(e)}";
         inAppBrowserRef.executeScript({
           code: cd
         }, null);
       }
       if (data.url.indexOf('uta-staging.unifyed.com/userinfo') != -1) {
         var cd = "try{webkit.messageHandlers.cordova_iab.postMessage(JSON.stringify({success:document.body.innerText}));}catch(e){alert(e)}";
         inAppBrowserRef.executeScript({
           code: cd
         }, null);
       }
     }

     inAppBrowserRef = window.open('https://idp.quicklaunchsso.com/oauth2/authorize?response_type=code&client_id=9iCjpVhq0ZfCCrso8NZu0yhVEW4a&scope=openid&redirect_uri=https://uta-staging.unifyed.com/auth/unifyd/passport/callback', "_blank", "clearcache=yes,clearsessioncache=yes,location=yes,enableViewportScale=yes,hardwareback=no,hidden=yes");
     inAppBrowserRef.addEventListener('message', messageCallBack);
     inAppBrowserRef.addEventListener('loadstop', loadStopCallBack);
     inAppBrowserRef.addEventListener('loadstart', loadStartCallBack);
     inAppBrowserRef.addEventListener("exit", exitCallBack);
   }

   $rootScope.loginToPortal();
 }

pages.pageprocessor:
function pageprocessorEvents(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

jQuery.fn.extend({

    size: function() {

      return this.length;

    }

  });

  $("#fixedFooterContent").html('');
  $.blockUI();
  var url = pagedef.url;
  $scope.page = 1;
  var weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  var monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  var dayStr;
  var monthStr;
  var dateStr;
  var currDate;

  var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
  var param = {
    "url": url
  };
  $http.post(rssreaderUrl, param, '').then(function successCallback(resp, status, headers, config) {
    $scope.feeds = resp.data.feed.entries;
    //$rootScope.feeds = data.feed.entries;
    var data = [];
    var index = 0;
    $.each($scope.feeds, function(key, val) {
      dayStr = "";
      monthStr = "";
      dateStr = "";
      if (val.pubDate) {
        currDate = new Date(val.pubDate);
        dayStr = weekDays[currDate.getDay()];
        monthStr = monthNames[currDate.getMonth()];
        dateStr = currDate.getDate();
      }
      data.push({
        "link": val.link,
        "displaydate": val.pubDate,
        //"formattedDate": moment(val.pubDate).format("ddd MMM DD"),
        "formattedDate": dayStr + " " + monthStr + " " + dateStr,
        "date": new Date(val.pubDate).getTime().toString(),
        // "currentDate" : new Date(val.pubDate),
        "title": val.title,
        "description": val.content,
        "index": key
      });
    });

    $rootScope.Eventsdata = data;
    $.unblockUI();

    $("#appContent").append($(pagedef.pageTemplate));
    $("#EventsListData").eventCalendar({
      jsonData: $rootScope.Eventsdata,
      showDescription: true,
      startWeekOnMonday: false,
      eventsLimit: 20,
      dataTemplate: pagedef.datatemplate,
      //cacheJson: false,
      moveSpeed: 0,
      moveOpacity: 0
    });
    $('.monthTitle').trigger('click');
    $('.arrow').click(function() {
      $('.monthTitle').trigger('click');
      setTimeout(function() {
        $('.eventsCalendar-loading').css('display', 'none');
        try {
          $scope.$apply();
        } catch (ex) {
          console.log("ex" + JSON.stringify(ex));
        }
      }, 500);
    });

    try {
      $scope.$apply();
    } catch (ex) {
      console.log(JSON.stringify(ex));
    } finally {
      console.log($('.monthTitle'));
    }
  },function errorCallBack(error, status, header, config) {
    //alert(JSON.stringify(error));
    $.unblockUI();
  });

}

function pageprocessorEventsDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
jQuery.fn.extend({

    size: function() {

      return this.length;

    }

  });
  $scope.selIndex = $routeParams.index;
  /*  $scope.title = $rootScope.Eventsdata[$routeParams.index - 1].title;
    $scope.displaydate = $rootScope.Eventsdata[$routeParams.index - 1].displaydate;
    $scope.description = $rootScope.Eventsdata[$routeParams.index - 1].description;
    $scope.link = $rootScope.Eventsdata[$routeParams.index - 1].link;*/
  $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
  console.log($scope.selEvent);
  $scope.openurl = function() {
    window.open($scope.selEvent.link, "_blank");
  };

  var data = pagedef.pageTemplate;
  data = $(data).append($(pagedef.datatemplate));
  console.log(JSON.stringify(data));
  var data1 = $compile($(data))($scope);
  $("#appContent").append(data1);

  $scope.trustHtml = function(data) {
    if (!data) {
      return data;
    }

    data = data.replace(/(?:\\r\\n|\\r|\\n)/g, '<br/>');
    data = data.replace(/(?:\\)/g, '');
    return $sce.trustAsHtml(data);
  }

  $scope.openFeed = function() {
    $location.path("/app/Events/Events");
    return;
    /*    $rootScope.Events = $scope.index;
        $rootScope.index = index;
        $scope.Eventsdata = $rootScope.Events.Eventsdata;
        console.log($scope.Eventsdata);
        $location.path("/app/Events/Events");*/
  };

  $scope.next = function() {
    var len = $rootScope.Eventsdata.length;
    console.log(len);
    try {
      console.log($scope.selIndex);
      if ($scope.selIndex < (len - 1)) {
        $scope.selIndex++;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }
      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };

  $scope.prev = function() {
    try {
      console.log($scope.selIndex);

      if ($scope.selIndex > 0) {
        $scope.selIndex--;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }

      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
}

