      method: "GET",
      headers: {
        "licenseKey": licenseKey
      }
    }
    $scope.dataloading = true;
    $.blockUI();
    $http(request).then(function(response) {
      console.log("RESPONSE" + JSON.stringify(response))
      console.log("response.data" + JSON.stringify(response.data))
      if (response.data) {
        $scope.semesterloaded = true;
        $scope.semesterinfo = response.data;
        $scope.dataloading = false;
        $scope.semesterinfo.currentlabel = getSemesterLabel(response.data.current_semester);
        $scope.semesterinfo.previouslabel = getSemesterLabel(response.data.previous_semester);
        $scope.semesterinfo.nextlabel = getSemesterLabel(response.data.next_semester);
        $scope.selectedSemester = $scope.semesterinfo.current_semester;
        $scope.selectedLabel = $scope.semesterinfo.currentlabel;
        $scope.selectSemester();
      }
      $.unblockUI();
    }, function(response) {
      $scope.error = response.data;
      $scope.dataloading = false;
      $scope.semesterloaded = true;
      $.unblockUI();
    });
