      $scope.classArrayData = [];
      $scope.callEventData = [];
      // console.log("$scope.callEventData" + $scope.callEventData)
      $scope.classes = response.data.class_array;

      $scope.classDataRecordDynamic = response.data.class_array;
      console.log("classDataRecordDynamic" + JSON.stringify($scope.classDataRecordDynamic));

      angular.element(document).ready(function() {


          var timer = setInterval(function() {
            console.log('TIMERgetDynamicData');
            console.log("LOADEDgetDynamicData....");
            $scope.getDynamicData();
            clearInterval(timer)
          }, 200)

        })
        //console.log($scope.classes); 
      var len = $scope.classes.length;
      //console.log(len);   
      $scope.dataloading = false;
      $.unblockUI();
      $scope.status = true;

      //Clear the data for all the days. Bug fix. 
      for (var i = 0; i < 7; i++) {
        $scope.weekdata[i].data = [];
      }
      angular.forEach($scope.classes, function(value, key) {
        updateEvents(value);
      });
      $scope.selectDay(new Date().getDay());
    }, function(response) {
      $scope.error = response.data;
      $scope.dataloading = false;
      $.unblockUI();
    });
