            $rootScope.startTime = moment(($rootScope.findDate).format("YYYYMMDD") + moment(start_time, "Hmm").format("Hmm"), "YYYYMMDD Hmm").format("YYYY-MM-DDTHH:mm:ss");
            $rootScope.endTime = moment(($rootScope.findDate).format("YYYYMMDD") + moment(end_time, "Hmm").format("Hmm"), "YYYYMMDD Hmm").format("YYYY-MM-DDTHH:mm:ss");

            eventToPush.title = value.field_of_study_abbreviation + " " + value.course_number;
