  function buildTermDropdown() {
    $scope.termDropDown = [];
    var tempDropDown = [];
    $scope.selterm = "Select a term";
    var tempTerm = "";
    var currentDate = new Date();
    angular.forEach($scope.canvasmycourses, function(value, key) {
      if (value.term && value.term.name && !itemInList(value.term.name, tempDropDown)) {
        if (value.term.end_at == null || (currentDate < new Date(value.term.end_at))) {
          tempTerm = value.term.name;
        }
        tempDropDown.push(value.term);
      }
    });
    var interimSortedColl = tempDropDown.sort(function(obj1, obj2) {
      return obj1.id - obj2.id;
    });
    angular.forEach(interimSortedColl, function(value, key) {
      //if (value.end_at == null || (currentDate < new Date(value.end_at))) {
      //  tempTerm = value.name;
      //}
      tempTerm = value.name;
      $scope.termDropDown.push(value.name)
    });
    console.log("Final current term: " + tempTerm);
    if ($scope.termDropDown && $scope.termDropDown.length > 0 && $rootScope.selectedCanvasTerm) {
      $scope.selterm = $rootScope.selectedCanvasTerm;
    } else {
      if (tempTerm) $scope.selterm = tempTerm;
