  $scope.discussionpage = 1;


  $scope.discussionsCount = 0;
  $scope.closedDiscussionsCount = 0;
  $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/discussion_topics/?order_by=recent_activity&per_page=50", "GET", {}, function(topics) {
    if (topics && topics.response && typeof topics.response == "string") {
      topics.response = JSON.parse(topics.response);
    }
    $scope.canvasSelCourceDTopics = topics.response;
    angular.forEach($scope.canvasSelCourceDTopics, function(value, key) {
      if (!value.locked_for_user) {
        $scope.discussionsCount++;
      } else {
        $scope.closedDiscussionsCount++;
      }
    });
  });



  $scope.openDiscussion = function(topic) {
    $rootScope.discussion_id = topic.id;
    $rootScope.canvasSelDiscussion = topic;
    $scope.discussionpage = 2;
    $location.path("/app/CanvasCourses/CanvasCoursesPage10");
