    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/discussion_topics/" + $rootScope.discussion_id, "GET", {}, function(discview) {
      if (discview && discview.response && typeof discview.response == "string") {
        discview.response = JSON.parse(discview.response);
      }
      $scope.canvasSelDiscussionView = discview.response;
    });
    */
  }


  // Page 2 functions
  $scope.trustHtml = function(html) {
    return $sce.trustAsHtml(html);
  }
  $scope.getAvator = function(id) {
    var avator = "";
    $.each($scope.canvasSelDiscussionView.participants, function(key, val) {
      if (val.id == id) {
        avator = val.avatar_image_url;
      }
    });
    return avator;
  }
  $scope.getAuthorName = function(id) {
    var name = "";
    $.each($scope.canvasSelDiscussionView.participants, function(key, val) {
      if (val.id == id) {
        name = val.display_name;
      }
    });
    return name;
  }

  $scope.openAttachment = function(att) {
    window.open(att.url, "_blank", "location=yes,hidden=no,clearcache=yes,enableViewportScale=yes");
  }

}

