  $scope.startTracking = function() {

    /*var GD = $.jStorage.get("GuardiansInfo");
    for (var i = 0; i < GD.items.length; i++) {
        $scope.SendPush(GD.items[i].phn)
    }*/

    $scope.SendPush();


    $scope.TrackMeStarted = true;
    var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/createTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
    var proxyMethod = "POST";

    var data = {
      "UserPhn": userphn,
      "trackingId": trackingID,
      "active": 'Yes'
    };
    var proxyData = data;
    $http({
      method: proxyMethod,
      url: url,
      data: proxyData
    }).
    success(function(data, status, headers, config) {
      trackingKey = data._id;
      $scope.sendTrackingLocation();
      trackMeInterval = setInterval($scope.sendTrackingLocation, 10000);

    }).
    error(function(data, status, headers, config) {
      $.unblockUI();
    });
  }

  $scope.sendTrackingLocation = function() {
    var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/createTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackMe";
    var proxyMethod = "POST";
    var Personaldata = $.jStorage.get('PersonalInfo');
    var userphn = Personaldata.PhNum;
    var name = Personaldata.FirstName + " " + Personaldata.LastName;
    var options = {
      timeout: 5000,
      frequency: 3000,
      enableHighAccuracy: true
    };
    navigator.geolocation.getCurrentPosition(function(loc) {
      var lat = loc.coords.latitude;
      var long = loc.coords.longitude;
      var data = {
        "userPhn": userphn,
        "Trackingid": trackingID,
        "User": name,
        "Lat": lat,
        "Long": long
      };
      var proxyData = data;
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        var trackID = trackingID;
        var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=ActiveGuardians";
        var proxyMethod = "POST";
        var data = {
          "userPhn": userphn,
          "trackingid": trackID,
        };
        var proxyData = data;
        $http({
          method: proxyMethod,
          url: url,
          data: proxyData
        }).
        success(function(data, status, headers, config) {
          //alert(JSON.stringify(data));
          $scope.GuardiansActiveList = data;
        }).error(function(data, status, headers, config) {
          $.unblockUI();
        });
      }).
      error(function(data, status, headers, config) {
        $.unblockUI();
      });
    }, onError, options);
  }

  $scope.finishTracking = function() {
    $scope.TrackMeStarted = false;
    var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/createTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=TrackingID";
    var proxyMethod = "POST";

    var data = {
      "_id": trackingKey,
      "UserPhn": userphn,
      "trackingId": trackingID,
      "active": 'No'
    };

    var proxyData = data;
    $http({
      method: proxyMethod,
      url: url,
      data: proxyData
    }).
    success(function(data, status, headers, config) {

    }).error(function(data, status, headers, config) {
      $.unblockUI();
    });
    clearInterval(trackMeInterval);
  }
}

