        var SendAlertLocation = function(loc) {

            //alert(JSON.stringify(loc));
            var message = '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3 style="color:white;">Sending Alert</h3></div>';
