                message: message
            });
            var lat = loc.coords.latitude;
            var long = loc.coords.longitude;

            var PI = $.jStorage.get("PersonalInfo");

            //alert(JSON.stringify(PI));
            var phn = PI.PhNum;

            UserName = PI.FirstName + " " + PI.LastName;

            var emailmsg = PI.FirstName + " " + PI.LastName + ": Help! I am in an emergency. Please see my location: <a href='http://maps.google.com/?q=" + loc.coords.latitude + "," + loc.coords.longitude + "'>http://maps.google.com/?q=" + loc.coords.latitude + "," + loc.coords.longitude + "</a>";
            var emergencyAlert = PI.FirstName + " " + PI.LastName + ": Help! I am in an emergency.";

            //alert(emailmsg);

            emailBody = "";
            emailBody = "<html><body>";
            emailBody += "<div>" + emailmsg + "</div><br/>";
            emailBody += "<div><img src='https://maps.googleapis.com/maps/api/staticmap?center=" + loc.coords.latitude + "," + loc.coords.longitude + "&zoom=16&size=600x300&maptype=roadmap&markers=color:blue|" + loc.coords.latitude + "," + loc.coords.longitude + "' /></div>";
            emailBody += "<br/><b>Directions to " + UserName + " location:</b> <a href='https://maps.google.com?saddr=Current+Location&daddr=" + loc.coords.latitude + "," + loc.coords.longitude + "'>Get Direction</a>";

            emailBody += "<br/><b>You can track the user with Track User function on the home screen if user has initiated Track Me function.</b>";
            emailBody += "<br/><br/>Thanks,<br/>McKendree Emergency";
            emailBody += "<br/><br/><div><img src='https://www.dropbox.com/s/py937zad36l7faa/App-icon%20copy.png?dl=1'/></div>";
            emailBody += "<br/><br/><div><img src='http://www.kryptosmobile.com/img/logo.png'/></div>";

            emailBody += "</body></html>";

            //alert(emailBody);

            var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=GuardiansList";
            var proxyMethod = "POST";

            var data = {
                "UserPhn": phn,
                "enabled": true
            };


            var proxyData = data;

            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
