                if (gudData.length == 0) {
                    navigator.notification.alert("Please add alert contacts.", null, 'Add Alert Contacts', 'Ok');
                    $.unblockUI();
                } else {

                    //alert(JSON.stringify(gudData));

                    var smsNumbers = "";
                    var emails = "";
                    var gphn = [];
                    var pushChannels = [];
                    for (var k = 0; k < gudData.length; k++) {
                        smsNumbers += gudData[k].GuardianPhn + ",";
                        emails += gudData[k].gudemail + ";";
                        gphn.push(gudData[k].GuardianPhn);
                        pushChannels.push(gudData[k].GuardianPhn);
                    }
                    //alert(JSON.stringify(pushChannels));
                    var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=UserProfiles";

                    //alert(url);
                    var proxyMethod = "POST";

                    var data = {
                        "phone": {
                            "$in": pushChannels
                        }
                    }

                    var proxyData = data;
                    $.blockUI();
                    $http({
                        method: proxyMethod,
                        url: url,
                        data: JSON.stringify(proxyData)
                    }).
                    success(function(data, status, headers, config) {
                        //alert(JSON.stringify(data));

                        smsNumbers = smsNumbers.substr(0, smsNumbers.length - 1);
                        emails = emails.substr(0, emails.length - 1);
                        var guardianInfo = [];
                        for (var i = 0; i < data.length; i++) {
                            guardianInfo.push({
                                'ID': data[i].pushID,
                                'type': data[i].deviceType
                            });
                        }
                        //alert(JSON.stringify(guardianInfo));

                        var pushData = {
                            "title": "McKendree Emergency Alert",
                            "body": UserName + ' is in emergency situation. Need help!',
                            "devices": guardianInfo,
                            "bundleId": "com.kryptosmobile.McKendree"
                        }

                        var emailData = {
                            'to': emails,
                            'subject': UserName + ' is in emergency situation. Need help!',
                            'body': emailBody,
                            'from': 'Donotreply@getbluelightapp.com'
                        };

                        //var url="https://push.kryptosmobile.com/kryptosds/push/sendPush";
                        $http.post("https://push.kryptosmobile.com/kryptosds/push/sendPush", pushData).success(function(response) {
                            $.unblockUI();
                            if (response.success) {
                                var xhr = $.ajax({
                                    url: "https://kryptos.kryptosmobile.com/gateway/CEAI/kryptosemailsender",
                                    contentType: 'application/json',
