                                    headers: {
                                        'licenseKey': 'UjpIaWVDVzpTOjE0NDk0ODk2MzUxMzE6VTpha0BjYW1wdXNlYWkub3JnOlQ6NDpQOjI4Ng=='
                                    },
                                    data: JSON.stringify(emailData),
                                    success: function(data) {
                                        $.unblockUI();
                                        if (data.response == "Successful") {
                                            //alert(JSON.stringify(smsNumbers));
                                            if (device.platform == 'iOS') {
                                                if (sms) {
                                                    sms.send(smsNumbers, emergencyAlert, msgOptions, alertContactssmssuccess, alertContactssmserror);
