function pageprocessorBlueLightEmergency(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  if ($.jStorage.get('termsCondition') == null || $.jStorage.get('termsCondition') == undefined) {
    $location.path("/app/TermsCondition/TermsCondition");
    return;
  }
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  var tenantid = $rootScope.metadata.tenantid;
  var mobappid = $rootScope.metadata.mobappid;
  var featureid = "87510";
  $rootScope.back = function() {
    $location.path("/home");
  };

  //$rootScope.defaultGuardians = ['911', '3028571110', '3024543938', '3025735418'];
  var nbOptions = 8;
  var angleStart = -360;

  // jquery rotate animation
  function rotate(li, d) {
    $({
      d: angleStart
    }).animate({
      d: d
    }, {
      step: function(now) {
        $(li)
          .css({
            transform: 'rotate(' + now + 'deg)'
          })
          .find('label')
          .css({
            transform: 'rotate(' + (-now) + 'deg)'
          });
      },
      duration: 0
    });
  }

  // show / hide the options
  function toggleOptions(s) {
    $(s).toggleClass('open');
    var li = $(s).find('li');
    var deg = $(s).hasClass('half') ? 180 / (li.length - 1) : 360 / li.length;
    for (var i = 0; i < li.length; i++) {
      var d = $(s).hasClass('half') ? (i * deg) - 90 : i * deg;
      $(s).hasClass('open') ? rotate(li[i], d) : rotate(li[i], angleStart);
    }
  }

  /*$('.selector button').click(function(e) {
      toggleOptions($(this).parent());
  });*/

  setTimeout(function() {
    toggleOptions('.selector');
  }, 100); //@ sourceURL=pen.js

  var UserName;
  var emailSuccess = smsSuccess = notifySuccess = false;
  var applicationId = "8bSjK36YW9cTRBNZOvp8mo3ANPskUW102DrS4BBt";
  var clientKey = "p8e83RAcb7n12eH4OspaFSpFLsnLdoOyCnxfyvLh";



  var AndroidDownloadLink = "https://play.google.com/store/apps/details?id=com.kryptosmobile.Emergency";
  var iosDownloadLink = "https://play.google.com/store/apps/details?id=com.kryptosmobile.Emergency";
  var msgOptions = {
    replaceLineBreaks: true, // true to replace \n by a new line, false by default
    android: {
      intent: '' // send SMS without open any other app
    }
  };
  var intent = '';
  //console.log(message);
  //leave empty for sending sms using default intent
  var success = function() {
    if (window.device) {
      smsSuccess = true;
      //navigator.notification.alert('Emergency Alert Sent to Guardians!', null, 'Alert', 'Ok');
    } else {
      smsSuccess = true;
      //alert('Emergency Alert Sent to Guardians!');
    }
  };
  var error = function(e) {
    if (window.device) {
      smsSuccess = false;
      //navigator.notification.alert('Emergency alert failed.' + e, null, 'Alert', 'Ok');
    } else {
      smsSuccess = false;
    }
  };
  $scope.AlertSend = function() {
    phonedialer.dial(
      "911",
      function(err) {
        if (err == "empty") alert("Unknown phone number");
        else alert("Dialer Error:" + err);
      },
      function(success) {
        alert('Dialing succeeded');
      }
    );
    if ($.jStorage.get("PersonalInfo") == null || $.jStorage.get("PersonalInfo") == undefined) {
      launchProfile();
      navigator.notification.alert("Please update your profile.", null, 'Update Profile', 'Ok');
      return;
    }

    var PI = $.jStorage.get("PersonalInfo");
    var phn = PI.PhNum;

    var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=GuardiansList";
    var proxyMethod = "POST";

    var data = {
      "UserPhn": phn
    };
    var proxyData = data;
    $.blockUI();
    $http({
      method: proxyMethod,
      url: url,
      data: proxyData
    }).
    success(function(data, status, headers, config) {
      var GD = [];
      var foundDefaultGuad = false;
      var defaultGD = $.jStorage.get("defaultGD");
      try {
        var defaultGD = $.jStorage.get("defaultGD");
        for (var i = 0; i < defaultGD.length; i++) {
          if (defaultGD[i].enable == true) {
            foundDefaultGuad = true;
          }
        }

        if (data.length == 0 && foundDefaultGuad == false) {
          navigator.notification.alert("Please add alert contacts or enable public safety contacts to send emergency alert.", null, 'Add Alert Contacts', 'Ok');
          $.unblockUI();
        } else {
          var options = {
            maximumAge: 0,
            timeout: 5000,
            enableHighAccuracy: true
          };
          navigator.geolocation.getCurrentPosition($scope.SendAlertLocation, onError, options);
        }
      } catch (e) {
        alert(e)
      }

    }).error(function(data, status, headers, config) {

    });
  }
  var onError = function(err) {
    //alert(JSON.stringify(err));
    $.unblockUI();
    switch (err.code) {
      case 3:
        {
          var errmsg = "Could not get the current position. Either GPS signals are weak or GPS has been switched off.";
          break;
        }
      case 1:
        {
          var errmsg = "Either GPS signals are weak or GPS has been switched off.";
          break;
        }
      case 2:
        {
          var errmsg = "Unable to get your current position.";
          break;
        }
    }
    if (window.device) {
      navigator.notification.alert(errmsg, null, 'GPS Error', 'Ok');
    } else {
      alert(errmsg);
    }
  }
  var launchProfile = function() {
    $location.path('/app/Profile2/Profile2')
  }
  var WithoutProfileSuccess = function() {
    if (window.device) {
      navigator.notification.alert('Emergency Alert sent! Ready to Configure KWC BlueLight Emergency? Get Started', launchProfile, 'Alert', 'Ok');
