      if ($.jStorage.get("SettingsData") == null) {
        var Settings = {};
        Settings.alertText = "Need Help."
        Settings.medicalInfo = false,
          Settings.additionalInfo = false,
          //Settings.defaultGuardian = true,
          Settings.searchRadius = '10',
          Settings.TrackMeInterval = '15'
        $.jStorage.set("SettingsData", Settings);

      }
