/* Radio button CSS */

.radioContainer {
  position: relative;
}

.radioContainer .radioItem {
  color: #767676;
  display: block;
  position: relative;
  padding: 12px 0;
}

.radioContainer .radioItem input[type=radio] {
  position: absolute;
  visibility: hidden;
}

.radioContainer .radioItem label {
  display: block;
  position: relative;
  font-weight: 300;
  font-size: 15px;
  z-index: 9;
  padding: 0 12px 0 24px;
  cursor: pointer;
  margin: 2px 0 0 0;
  -webkit-transition: all 0.25s linear;
}

.radioContainer .radioItem:hover label {
  color: #000;
}

.radioContainer .radioItem .check {
  display: block;
  position: absolute;
  border: 1px solid #767676;
  border-radius: 100%;
  height: 18px;
  width: 18px;
  top: 15px;
  left: 0;
  z-index: 5;
  transition: border .25s linear;
  -webkit-transition: border .25s linear;
}

.radioContainer .radioItem:hover .check {
  border: 1px solid #000;
}

.radioContainer .radioItem .check::before {
  display: block;
  position: absolute;
  content: '';
  border-radius: 100%;
  height: 12px;
  width: 12px;
  top: 2px;
  left: 2px;
  margin: auto;
  transition: background 0.25s linear;
  -webkit-transition: background 0.25s linear;
}

.radioContainer input[type=radio]:checked ~ .check {
  border: 1px solid #000;
}

.radioContainer input[type=radio]:checked ~ .check::before {
  background: #000;
  background-color: #000;
}

.radioContainer input[type=radio]:checked ~ label {
  color: #000;
}


/***** Checkbox CSS *****/

.control {
  display: block;
  position: relative;
  padding-left: 30px;
  cursor: pointer;
  font-size: 14px;
}

.control input {
  position: absolute;
  z-index: -1;
  opacity: 0;
}

.control__indicator {
  position: absolute;
  top: 0;
  left: 0;
  height: 18px;
  width: 18px;
  border-radius: 2px;
  border: #767676 solid 1px !important;
}

.control:hover input ~ .control__indicator,
.control input:focus ~ .control__indicator {
  border: #252729 solid 1px;
}

.control input:checked ~ .control__indicator {
  background: #252729;
  background-color: #252729;
  border: 0;
}

.control:hover input:not([disabled]):checked ~ .control__indicator,
.control input:checked:focus ~ .control__indicator {
  background: #252729;
  background-color: #252729;
  border: 0;
}

.control__indicator:after {
  content: '';
  position: absolute;
  display: none;
}

.control input:checked ~ .control__indicator:after {
  display: block;
}

.control--checkbox .control__indicator:after {
  left: 6px;
  top: 2px;
  width: 6px;
  height: 10px;
  border: solid #fff;
  border-width: 0 2px 2px 0;
  transform: rotate(45deg);
}

.control--checkbox input:disabled ~ .control__indicator:after {
  border-color: #7b7b7b;
}

