function pageprocessorBevoBucks(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  if (window.device) {
    $.blockUI();
    screen.orientation.unlock();
  }
  var iabRef = window.open(pagedef.url, "_blank", "location=no,hidden=no,enableViewportScale=yes,clearcache=yes");
  var hideBlockUi = function() {
    $.unblockUI();
    iabRef.show();
  };
  var iabClose = function(data) {
    $.unblockUI();
    window.screen.orientation.lock('portrait');
    iabRef.removeEventListener("exit", iabClose);
    //window.location.href = "index.html#home";
    window.history.back();
  };
  iabRef.addEventListener("exit", iabClose);
  var loadStop = function(data) {
    hideBlockUi();
    iabRef.removeEventListener("loadstop", loadStop);
  };
  iabRef.addEventListener("loadstop", loadStop);
}

