  var getSemesterLabel = function(year) {
    var semDisplayName = '';
    if (year.endsWith(6)) {
      semDisplayName = "Summer";
    } else if (year.endsWith(2)) {
      semDisplayName = "Spring";
    } else if (year.endsWith(9)) {
      semDisplayName = "Fall";
    }
    return semDisplayName + " " + year.slice(0, -1);
  };
