    money2 = (diningInfo["#send-data"][0]["plans"][1]["plan-balance"]);
    planDescription2 = (diningInfo["#send-data"][0]["plans"][1]["plan-description"]);
    money3 = (diningInfo["#send-data"][0]["plans"][2]["plan-balance"]);
    planDescription3 = (diningInfo["#send-data"][0]["plans"][2]["plan-description"]);
    plan(money1, '$');
    include(money2, '$');
    $scope.dollarAmount1 = money1;
    $scope.moneyFoundPlan1 = dollarSignFound;
    $scope.planDescription1 = planDescription1;
    if (plan1 == false) {
      $scope.dollarAmount1 = "No plan found";
      $scope.planDescription1 = "None";
    }
    $scope.moneyFoundPlan2 = dollarSignFound;
    $scope.dollarAmount2 = money2;
    $scope.planDescription2 = planDescription2;
    include2(money3, 'Y');
    $scope.moneyFoundPlan3 = dollarSignFound2;
    $scope.dollarAmount3 = money3;
    $scope.planDescription3 = planDescription3;
    $scope.accountinfoloaded = true;
    $scope.dataloading = false;
    $scope.status = true;
    $.unblockUI();
  }, function(response) {
    $scope.error = response.data;
    $scope.dataloading = false;
    $scope.accountinfoloaded = true;
    $.unblockUI();
  });
}

