            var exitCallBack = function(data) {
                $.unblockUI();
            };
            var hideBlockUi = function(url) {
                var code = url.substring(url.indexOf('code=') + 5);
                inAppBrowserRef.close();
                var req = {
                    "method": 'POST',
                    "url": canvasBaseUrl + '/login/oauth2/token?grant_type=authorization_code&client_id=' + clientid + '&client_secret=' + clientsecret + '&redirect_uri=' + callbackUrl + '&code=' + code,
                    "headers": {
                        'Content-Type': 'application/x-www-form-urlencoded'
                    }
                };
                console.log('apiEndPoint', req);
                $http(req).then(function successCallback(res) {
                    console.log(res);
                    return callback(res.data);
                }, function errorCallback(err) {
                    console.log(JSON.stringify(err));
                    if (!$rootScope.isblocking) {
                        $.unblockUI();
                    }
                    //return callback();
                    /* Commented the code below since token refersh is not working */
                    if (err.status == 404) {
                        return callback(null);
                    } else if (err.status == 0 || err.status == 401 || err.status == -1) {
                        $rootScope.refreshToken(url, method, data, callback);
                        //return callback();
                    } else {
                        callback(err);
                    }
                });
            };
            var cb = function(data) {
                //alert('cb');
            };
            var messageCallBack = function(params) {
                hideBlockUi();
            }
            var loadStartCallBack = function(data) {
                console.log('loadStartCallBack', data);
                if (data.url.indexOf('passport/callback?code=') != -1) {
                    hideBlockUi(data.url);
                }
                if (data.url.indexOf('instructure.com/login') != -1) {
                    inAppBrowserRef.show();
                }
            }
            var loadStopCallBack = function(data) {
                console.log('loadStopCallBack', data);
                if (data.url.indexOf('s1') != -1) {
                    //var cd = "document.getElementById('username').value ='" + $.jStorage.get("unifyedusername").split('@')[0] + "';document.getElementById('password').value = '" + $.jStorage.get("unifyedpassword") + "';document.getElementsByName('_eventId_proceed')[0].click();";
                    var cd = "document.getElementById('username').value ='" + $.jStorage.get("username") + "';document.getElementById('password').value = '" + $.jStorage.get("password") + "';document.getElementsByName('_eventId_proceed')[0].click();";
                    inAppBrowserRef.executeScript({
