                    }, null);
                }
                if (data.url.indexOf('s2') != -1) {
                    inAppBrowserRef.show();
                }
                if (data.url.indexOf('oauth2/confirm') != -1) {
                    inAppBrowserRef.show();
                    var cd = "document.getElementsByTagName('button')[0].click();document.getElementsByName('commit')[0].click();";
                    inAppBrowserRef.executeScript({
                        code: cd
                    }, null);
                }
            };

            inAppBrowserRef = window.open(canvasBaseUrl + '/login/oauth2/auth?client_id=' + clientid + '&response_type=code&redirect_uri=' + callbackUrl, "_blank", "clearcache=no,clearsessioncache=no,location=yes,enableViewportScale=yes,hardwareback=no,hidden=yes");
            inAppBrowserRef.addEventListener('message', messageCallBack);
            inAppBrowserRef.addEventListener('loadstop', loadStopCallBack);
            inAppBrowserRef.addEventListener('loadstart', loadStartCallBack);
            inAppBrowserRef.addEventListener("exit", exitCallBack);
        }

        var counter = 0;
        /****************************************************************************************************************************************/

        $rootScope.getCanvasRefreshToken = function(callBack) {
            $.blockUI();
            var endpoint = "/login/oauth2/token";
            var url = canvasBaseUrl + endpoint;
            if (endpoint.indexOf(canvasBaseUrl) != -1) {
                url = endpoint;
            }
            var authpostdata = "grant_type=refresh_token&client_id=" + clientid + "&client_secret=" + clientsecret + "&refresh_token=" + $rootScope.canvasrefreshtoken;
            var apiData = {
                "authService": "canvasserviceadapter",
                "url": url,
                "method": "POST",
                "access_token": $rootScope.canvasaccesstoken,
                "postData": authpostdata,
                "tenant": tenantId,
                "licenseKey": licenseKey
            }
            $http({
                method: "POST",
                url: apiurl,
                headers: {
                    "Content-Type": "application/json",
                    "X-TENANT-ID": tenantId
                },
                data: apiData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                if (data && data.response && typeof data.response == "string") {
                    data.response = JSON.parse(data.response);
                }
                console.log("refresh token:");
                console.log(data.response);
                $rootScope.canvasaccesstoken = data.response.access_token;
                console.log("$rootScope.canvasaccesstoken=" + $rootScope.canvasaccesstoken);
                var canvasToken = $.jStorage.get("CanvasAuthToken");
                canvasToken.access_token = data.response.access_token;
                $.jStorage.set("CanvasAuthToken", canvasToken);
                callBack(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                $.jStorage.deleteKey("CanvasAuthToken");
                $rootScope.canvasaccesstoken = null;
                $rootScope.canvasrefreshtoken = null;
                canvasAuth(function(res) {
                    var result = res;
                    if (!result || result.error) {
                        $.unblockUI();
                        if (window.device) {
                            navigator.notification.alert("Canvas doesn't have an account for you, please contact your administrator", null, 'Canvas Auth Error', 'Ok');
                        } else {
                            alert('Error authenticating to canvas. Please contact Administrator.');
                        }
                        $location.path("/app/Menu82/Menu82");
                        return;
                    }
                    $rootScope.canvasaccesstoken = result.access_token;
                    $rootScope.canvasrefreshtoken = result.refresh_token;
                    $.jStorage.set("CanvasAuthToken", result);
                    loadCourses();
                });
            });
        }


        $rootScope.invokeCanvasAPI = function(endpoint, method, postdata, callback) {
            $.blockUI();
            var url = canvasBaseUrl + endpoint;
            if (endpoint.indexOf(canvasBaseUrl) != -1) {
                url = endpoint;
            }
            var apiData = {
                "authService": "canvasserviceadapter",
                "url": url,
                "method": method,
                "access_token": $rootScope.canvasaccesstoken,
                "postData": "",
                "tenant": tenantId,
                "licenseKey": licenseKey
            }
            $http({
                method: "POST",
                url: apiurl,
                headers: {
                    "Content-Type": "application/json",
                    "X-TENANT-ID": tenantId
                },
                data: apiData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                if (data && data.response && typeof data.response == "string") {
                    data.response = JSON.parse(data.response);
                }
                if (data.message && data.message == "401 Unauthorized") {
                    counter++;
                    if (counter > 2) {
                        callback(data, status, headers, config);
                    } else {
                        counter++;
                        $rootScope.getCanvasRefreshToken(function(data, status, headers, config) {
                            $rootScope.invokeCanvasAPI(endpoint, method, postdata, callback);
                        });
                    }
                } else {
                    callback(data, status, headers, config);
                }
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                if (status == 401 && data.errors && data.errors.length > 0 && data.errors[0].message === 'Invalid access token.') { //Ticket expired.. need to relogin
                    var authpostdata = {
                        "grant_type": "refresh_token",
                        "client_id": clientid,
                        "client_secret": clientsecret,
                        "refresh_token": accesstoken //$.jStorage.get("CanvasAuthToken").refresh_token
                    };
                    $http.post(canvasBaseUrl + "/login/oauth2/token", authpostdata).success(function(data, status, headers, config) {
                        var curStoredToken = $.jStorage.get("CanvasAuthToken");
                        curStoredToken.access_token = data.access_token;
                        $.jStorage.set("CanvasAuthToken", curStoredToken);
                        $rootScope.invokeCanvasAPI(endpoint, method, postdata, callback);
                        $.unblockUI();
                    }).error(function(data, status, headers, config) {
                        $.unblockUI();
                        $.jStorage.deleteKey('CanvasAuthToken');
                        //$rootScope.$apply(function() {
                        //  $location.path("/CanvasLogin/CanvasLogin");
                        //});
                    });
                } else {
                    callback(data, status, headers, config);
                }
            });
        }
        /****************************************************************************************************************************************/
        // openCourse function is not in use now, just keeping in case if design needs to be rolled back
        $scope.openCourse = function(course) {
