      sclass = 'fa-copy';
    } else if (menu.id == 'syllabus') {
      sclass = 'fa-file';
    }
    return sclass;
  }

  $scope.openMenu = function(menu) {
    console.log(menu)
    if (menu.id == 'home') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage3");
    } else if (menu.id == 'modules') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage4");
    } else if (menu.id == 'announcements') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage5");
    } else if (menu.id == 'assignments') {
      console.log("Opening assignments page...");
      $location.path("/app/CanvasCourses/CanvasCoursesPage12");
    } else if (menu.id == 'people') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage7");
    } else if (menu.id == 'discussions') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage8");
    } else if (menu.id == 'grades') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage6");
    } else if (menu.id == 'conferences') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage9");
    } else if (menu.id == 'pages') {
      $location.path("/app/CanvasMyCourses/CanvasMyCoursesPage7");
    } else if (menu.id == 'files') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage11");
    } else if (menu.id == 'syllabus') {
      $location.path("/app/CanvasCourses/CanvasCoursesPage13");
    }else {
      navigator.notification.alert(menu.label + " is not currently supported.",
        null, 'Unsupported feature', 'Ok');
    }
  }

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  if (true) {
    return;
  }


  $scope.trustHtml = function(html) {
    return $sce.trustAsHtml(html);
  }

  $scope.modflag = false;
  $scope.homeflag = true;
  $scope.annflag = false;

  $scope.showModules = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/modules/?include[]=items&include[]=content_details", "GET", {}, function(modules) {
      $scope.canvasSelCourcemodules = modules.response;
    });
    $scope.modflag = true;
    $scope.homeflag = false;
    $scope.annflag = false;
  }

  $scope.showHome = function() {
    $scope.modflag = false;
    $scope.homeflag = true;
    $scope.annflag = false;
  }

  $scope.showAnnouncements = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/discussion_topics/?only_announcements=true", "GET", {}, function(announcements) {
      $scope.canvasSelCourceannouncements = announcements.response;
    });
    $scope.modflag = false;
    $scope.homeflag = false;
    $scope.annflag = true;
  }

  var accesstoken = $.jStorage.get("CanvasAuthToken");
  if (accesstoken) {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/front_page", "GET", {}, function(frontpage) {
      $scope.canvasSelCourceFrontPage = frontpage.response;
    });
    return;
  } else {
    $location.path("/app/CanvasLogin/CanvasLogin");
  }
}

