function pageprocessorCanvasCoursesPage4(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  console.log("in assignments page...");
  $scope.isDirect = false;

  $scope.canvasSelCourseId = $rootScope.canvasSelCourse.id;

  var getCourse = function(id) {
    var course = null;
    $.each($scope.canvasmycourses, function(key, val) {
      if (id == val.id) {
        course = val;
      }
    });
    return course;
  }

  $scope.dropdownselect = function(id) {
    $rootScope.canvasSelCourse = getCourse($scope.canvasSelCourseId);
    $scope.showOverdue();
  }

  $scope.trustHtml = function(html) {
    return $sce.trustAsHtml(html);
  }

  $scope.odflag = true;
  $scope.upflag = false;
  $scope.unflag = false;
  $scope.paflag = false;

  $scope.showOverdue = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=overdue", "GET", {}, function(assignments) {
      $scope.canvasSelCourceODAssn = assignments;
    });
    $scope.odflag = true;
    $scope.upflag = false;
    $scope.unflag = false;
    $scope.paflag = false;
  }

  $scope.showUpcoming = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=upcoming", "GET", {}, function(assignments) {
      $scope.canvasSelCourceUPAssn = assignments;
    });
    $scope.odflag = false;
    $scope.upflag = true;
    $scope.unflag = false;
    $scope.paflag = false;
  }

  $scope.showUndated = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=undated", "GET", {}, function(assignments) {
      $scope.canvasSelCourceUNAssn = assignments;
    });
    $scope.odflag = false;
    $scope.upflag = false;
    $scope.unflag = true;
    $scope.paflag = false;
  }

  $scope.showPast = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=past", "GET", {}, function(assignments) {
      $scope.canvasSelCourcePAAssn = assignments;
      $scope.odflag = false;
      $scope.upflag = false;
      $scope.unflag = false;
      $scope.paflag = true;
    });
  }

  $scope.openAssignment = function(item) {
    if (item.quiz_id) {
      $rootScope.quizItem = item;
      $location.path("/app/CanvasQuizzes/CanvasQuizzesPage2");
    } else if (item.discussion_topic) {
      $rootScope.discussion_id = item.discussion_topic.id;
      $rootScope.canvasSelDiscussion = item.discussion_topic;
      $location.path("/app/CanvasDiscussions/CanvasDiscussionsPage2");
    } else {
      $rootScope.assigmentItem = item;
      $location.path("/app/CanvasAssignments/CanvasAssignmentsPage4");
    }
  };


  $scope.openCourse = function(course) {
    $rootScope.canvasSelCourse = course;
    $location.path("/app/CanvasAssignments/CanvasAssignmentsPage2");
  }


  $scope.checkAvailability = function(date) {
    if (date == null) {
      return
    }
    var lockDate = new Date(date);
    var d = new Date();
    if (lockDate > d) {
      var a = lockDate.toString();
      return "Available until: " + a.substr(4, 6);
    } else {
      return "Closed"
    }
  }

  $scope.dueDate = function(date) {
    if (date == null) {
      return
    }
    var d = new Date(date);
    d = d.toString();
    return "Due at: " + d.substr(4, 6);
  }

}

