      }
    });
    //   $scope.odflag = false;
    // $scope.upflag = false;
    //  $scope.paflag = false;
  }

  $scope.showPast = function() {
    $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/assignments/?bucket=past", "GET", {}, function(assignments) {
      $scope.canvasSelCourcePAAssn = assignments.response;
      // alert(JSON.stringify($scope.canvasSelCourcePAAssn))
