    }
    var lockDate = new Date(date);
    var d = new Date();
    if (lockDate > d) {
      var a = lockDate.toString();
      return "Available until: " + a.substr(4, 6);
    } else {
      return "Closed"
    }
  }

  $scope.dueDate = function(date) {
    if (date == null) {
      return
    }
    var d = new Date(date);
    var hours = d.getHours();
    var minutes = d.getMinutes();
    var ampm = hours >= 12 ? 'pm' : 'am';
    hours = hours % 12;
    hours = hours ? hours : 12; // the hour '0' should be '12'
    minutes = minutes < 10 ? '0' + minutes : minutes;
    var strTime = hours + ':' + minutes + ' ' + ampm;
    d = d.toString();
    //alert(d)
    return d.substr(4, 6) + " at " + strTime;
  }


  $scope.init = function() {
    $scope.showOverdue();
    $scope.showPast();
    $scope.showUndated();
    $scope.showUpcoming();

  }

  $scope.init();

}

