function pageprocessorMenu82(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $("#fixedFooterContent").html('');
  $rootScope.appDisplayName = $rootScope.mobileAppName;
  //Commenting this unblockui. Not sure why we need this.
  if (!$rootScope.loggedin) {
    setTimeout(function() {
      $.unblockUI();
    }, 500);

  } else {
    setTimeout(function() {
      $.unblockUI();
    }, 3000);
  }
  $rootScope.from = "";
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  var btm = $('.footerGroupIcons').height() + 11;
  $('#appContent').css('padding-bottom', btm - 1 + 'px');

  $scope.logout = function() {
    try {
      $scope.confirm = function(btn) {
        if (btn == 1) {
          $rootScope.loggedIn = false;
          $rootScope.ticket = null;
