          $.jStorage.deleteKey('username');
          $.jStorage.deleteKey('password');
          $.jStorage.deleteKey('ticket');
          $location.path("/app/SignIn279/SignIn279");

        } else {
          //$location.path("/home");
        }
        $scope.$apply();
      }
      if (window.device)
        navigator.notification.confirm("Are you sure you want to logout?", $scope.confirm, 'Logout', ['Yes', 'No']);
      else {
        var retVal = confirm("Do you want to continue ?");
        if (retVal == true) {
          $location.path("/app/SignIn279/SignIn279");
        } else {

        }
      }
    } catch (e) {
      alert(e)
    }
  }

  $scope.loginPage = function() {
    $location.path("/app/SignIn279/SignIn279");
  }
  $scope.addMarkerIcon = function(lastPubDate) {
    if ($.jStorage.get("lastNotificationDate")) {
      if (lastPubDate != $.jStorage.get("lastNotificationDate")) {
        $rootScope.notificationFeeds = lastPubDate;
        setTimeout(function() {
          $.unblockUI();
          var msgCount = "!";
          var badgeText = '<span class="badge" style="position:absolute;right:20px;top:10px;font-size:16px;background:yellow;color:red;font-weight: bolder">' + msgCount + '<\/span>';
          $("a[href$='#app/Admissions56/Admissions56'] img").after(badgeText);
          //$.jStorage.set("lastNotificationDate", lastPubDate);
        }, 500);
      }
    } else {
      $rootScope.notificationFeeds = lastPubDate;
      setTimeout(function() {
        $.unblockUI();
        var msgCount = "!";
        var badgeText = '<span class="badge" style="position:absolute;right:20px;top:10px;font-size:16px;background:yellow;color:red;font-weight: bolder">' + msgCount + '<\/span>';
        $("a[href$='#app/Admissions56/Admissions56'] img").after(badgeText);
        //$.jStorage.set("lastNotificationDate", lastPubDate);
      }, 500);
    }
  }
  $scope.getNotificationData = function() {
    /*added to push xml data - start*/
    $.blockUI();
    if (window.device) {
      var feedUrl = "https://emergency.utexas.edu/emergency/status.xml?m=" + Math.random(2);
      //var feedUrl = "https://sandbox.emergency.utexas.edu/emergency/status.xml";
    } else {
      var feedUrl = "/websimulator/xml?url=https://emergency.utexas.edu/emergency/status.xml?m=" + Math.random(2);
    }
    var lastPubDate = "";
    var xhr = $.ajax({
      url: feedUrl,
      dataType: "xml",
      async: true,
      data: {},
      success: function(resp) {
        var results = $.xml2json(resp);
        if (results.channel.item) {
          $scope.feeds = results.channel.item;
          if ($scope.feeds.pubDate) {
            lastPubDate = $scope.feeds.pubDate;
            $scope.addMarkerIcon(lastPubDate);
          } else {
            lastPubDate = $scope.feeds[0].pubDate;
            $scope.addMarkerIcon(lastPubDate);
          }
        } else {
          $.unblockUI();
        }
      },
      error: function(e) {
        $.unblockUI();
      }
    });
    /*added to push xml data - end*/
  }
  $scope.getNotificationData();
}

