function pageprocessorEvents(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  jQuery.fn.extend({

    size: function() {

      return this.length;

    }

  });

  $("#fixedFooterContent").html('');
  $.blockUI({
    message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div><h3 style="color:#fff;">Loading Events...</h3></div>'
  });
  var url = pagedef.url;
  $scope.page = 1;
  var weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  var monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  var dayStr;
  var monthStr;
  var dateStr;
  var currDate;

  var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
  var param = {
    "url": url
  };
  $http.get(url + "&page=1").then(function successCallback(resp, status, headers, config) {
    var total = 2;
    total = resp.data.page.total;
    var allData = [];
    allData = allData.concat(resp.data.events);
    $scope.feeds = allData;
    var count = 1;
    for (var itr = 2; itr <= total; itr++) {
      $http.get(url + "&page=" + itr).then(function successCallback(resp, status, headers, config) {
        count++;
        allData = allData.concat(resp.data.events);
        $scope.feeds = allData;
        if (count == total) {
          var data = [];
          var index = 0;
          $.each($scope.feeds, function(key, val) {
            if (val == undefined || val == 'undefined') {

            } else {
              val = val.event;
              dayStr = "";
              monthStr = "";
              dateStr = "";
              if (val.event_instances[0].event_instance.start) {
                currDate = new Date(val.event_instances[0].event_instance.start);
                dayStr = weekDays[currDate.getDay()];
                monthStr = monthNames[currDate.getMonth()];
                dateStr = currDate.getDate();
              }
              data.push({
                "link": val.url,
                "displaydate": val.event_instances[0].event_instance.start,
                //"formattedDate": moment(val.pubDate).format("ddd MMM DD"),
                "formattedDate": dayStr + " " + monthStr + " " + dateStr,
                "date": new Date(val.event_instances[0].event_instance.start).getTime().toString(),
                // "currentDate" : new Date(val.pubDate),
                "title": val.title,
                "description": val.description,
                "index": key
              });
            }
          });

          $rootScope.Eventsdata = data;
          $.unblockUI();

          $("#appContent").append($(pagedef.pageTemplate));
          $("#EventsListData").eventCalendar({
            jsonData: $rootScope.Eventsdata,
            showDescription: true,
            startWeekOnMonday: false,
            eventsLimit: 500,
            dataTemplate: pagedef.datatemplate,
            //cacheJson: false,
            moveSpeed: 0,
            moveOpacity: 0
          });
          $('.monthTitle').trigger('click');
          $('.arrow').click(function() {
            $('.monthTitle').trigger('click');
            setTimeout(function() {
              $('.eventsCalendar-loading').css('display', 'none');
              try {
                $scope.$apply();
              } catch (ex) {
                console.log("ex" + JSON.stringify(ex));
              }
            }, 500);
          });

          try {
            $scope.$apply();
