          alert('No More Events')
        }
      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };

  $scope.prev = function() {
    try {
      console.log($scope.selIndex);

      if ($scope.selIndex > 0) {
        $scope.selIndex--;
        $scope.selEvent = $rootScope.Eventsdata[$scope.selIndex];
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More Events', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('No More Events')
        }

      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
}

